		function object2String(obj) {
			var val, output = "";
			if (obj) {    
				output += "{";
				for (var i in obj) {
					val = obj[i];
					switch (typeof val) {
						case ("object"):
							if (val[0]) {
								output += i + ":" + array2String(val) + ",";
							} else {
								output += i + ":" + object2String(val) + ",";
							}
							break;
						case ("string"):
							output += i + ":'" + escape(val) + "',";
							break;
						default:
							output += i + ":" + val + ",";
					}
				}
				output = output.substring(0, output.length-1) + "}";
			}
			return output;
		}
