function getTrainInfo (){
  var ret = {};
  ret.trainUnts = [];
  for (i=0; i<16; i++){
    ret.trainUnts[i] = 0;
  }
    var q = Seed.queue_unt;
  for(i=0; i<Cities.numCities; i++) {   // each city
    cityID = 'city'+ Cities.cities[i].id;
	q = Seed.queue_unt[cityID];
	if (q && q.length>0){
	  for (qi=0; qi<q.length; qi++)
          ret.trainUnts[q[qi][0]] += parseInt(q[qi][1]);
	  }
    }
  return ret;
}

