function thouormil(value,roundToK){
	if (value == 0)
		return '0';
	else if (value == '')
		return '';
	else if (value != parseFloat(value))
		return value;
	var val=value;
	if (roundToK && val > 9999)
		val = 1000 * Math.round(val/1000);
	if ((val%1000000)==0)
		return addCommas(val/1000000)+'m';
	else if ((val%1000)==0)
		return addCommas(val/1000)+'k';
	else
		return addCommas(val);
}

