/* Message Notifier script
 * Makes Derpy notify you vocally when there's an unread letter.
 * Audio file and function by RogueDarkJedi
 * Modified for powdertoy.co.uk by boxmein
 * Functions:
 * init() - Tests if there's a CUnreadCount element on the screen and then plays a sound.
 * playAudioFile() - Plays a sound
 */
var letterNotifier = {
	noSound: false, //IDK WHY
	soundFile: "data:video/ogg;base64,",
    
    divStyle: "display: block; position: fixed; top: 20px; left: 20px; height: 34px; padding: 0px 10px; float: right; "+
                "background-color: rgb(255,0,0); font-family: sans-serif; font-weight: bold; color: white;",





	init: function() {
		if(document.getElementsByClassName("CUnreadCount").length != 0 || document.getElementsByClassName("icon-envelope").length != 0)
		{
		  this.playAudioFile();
		  this.createDiv();
		}
	},
	
	playAudioFile: function()
    {
      if(this.noSound)
        return;
        
		var audioFile = document.createElement("audio");
		audioFile.setAttribute("id", "derpletteralert");
		audioFile.setAttribute("autoplay", true);
		audioFile.setAttribute("src", this.soundFile);
		document.body.appendChild(audioFile);
		
    },
    createDiv: function() 
    {
        var div = document.createElement("div");
        div.setAttribute("style", this.divStyle);
        div.innerHTML = "You got mail.";
        document.body.appendChild(div);
    }
        
};
letterNotifier.init();
