			function set_map_list(base_x, base_y, base_name) {
				var $maplist_box = $('#ixamoko_maplist1');
				$maplist_box.empty();
				var map_list = {};
				if (localStorage.getItem("map_list")) {
					map_list = secureEvalJSON(localStorage.getItem("map_list"));
				}
				var cc = {'織田家': 1,'足利家': 2,'黒田家': 2,'武田家': 3,'上杉家': 4,'徳川家': 5,'毛利家': 6,'伊達家': 7,'浅井家': 7,
						'北条家': 8,'長宗我部家': 9,'島津家': 10,'豊臣家': 11,'大友家': 11,'最上家': 12,'石田家': 12};
				var dist = 0;
				var tmp;
				for (var key in map_list) {
					tmp = key.match(/x=(\-?\d+)&y=(\-?\d+)&c=(\d+)/);
					if (!tmp) {
						tmp = key.match(/(-?\d+),(-?\d+)/);
					}
					dist = Math.sqrt(Math.pow(parseInt(tmp[1], 10) - base_x, 2) + Math.pow(parseInt(tmp[2], 10) - base_y, 2));
					dist = Math.floor(dist * 10) / 10;
					var newCountry = $('#ig_new_map_country');
					if (tmp.length === 4 && parseInt(tmp[3]) !== cc[$('#ig_map_country p.map_name').text().replace(/■/g,"").trim()] && 0 === newCountry.size()) {
						dist = "*";
					}
					var reg = $('<DIV coord="' + key + '" style="cursor:pointer;padding:3px 4px;margin:1px;border-radius:2px;"><span style="font-weight:bold;font-family:Verdana;">' + map_list[key] + '</span><div class="coorddist"><span class="distance_range">[' + dist + ']</span> (' + key + ')</div></DIV>')
							.click(mouseclick).appendTo($maplist_box);
					if (dist !== "*") {
						reg.hover(mouseover, mouseout);
					}
				}
				function mouseover() {
					$(this).css('background-color', '#937824');
					if (options.map_minimap) {
						var ctx = $('#ixamoko_maplist3 canvas')[0].getContext('2d');
						var coord = $(this).attr('coord').match(/(\-?\d+).+?(\-?\d+)/);
						ctx.fillStyle = "red";
						ctx.beginPath();  
						ctx.arc((parseInt(coord[1]) + 180)/360*150 + 5, (180 - parseInt(coord[2]))/360*120 + 5, 3, 0, Math.PI*2, true);
						ctx.fill();
					}
				}
				function mouseout() {
					$(this).css('background-color', '');
					if (options.map_minimap) {
						var ctx = $('#ixamoko_maplist3 canvas')[0].getContext('2d');
						var coord = $(this).attr('coord').match(/(\-?\d+).+?(\-?\d+)/);
						coord = [(parseInt(coord[1]) + 180)/360*150 + 5, (180 - parseInt(coord[2]))/360*120 + 5];
						ctx.clearRect(coord[0]-4, coord[1]-4, 8, 8);
					}
				}
				function mouseclick() {
					var coord = $(this).attr('coord').match(/(\-?\d+),(\-?\d+)/);
					var ajaxurl;
					if (coord) {
						ajaxurl = '/map.php?x=' + coord[1] + '&y=' + coord[2];
					} else {
						ajaxurl = '/map.php?' + $(this).attr('coord');
					}
					map_Ajax_Move(ajaxurl);
				}
			}

			//☆リスト
			function map_list2(base_x, base_y, base_name) {
				var $status_box = $('#ixamoko_maplist2');
				$status_box.empty();
				//		$status_box.append('<DIV style="background-color:#faf;margin-top:3px;font-weight:bold;">[選択拠点: '+base_name+']</DIV>');

				var HOSHI_SET = [],
				hoshi = '★',
				i;
				if (options.map_starx < 6) {
					for (i = 0; i < options.map_starx; ++i) {
						HOSHI_SET.push(hoshi);
						hoshi += '★';
					}
				} else if (options.map_starx == 6) {
					hoshi = '★★★★★★';
					HOSHI_SET.push(hoshi);
				} else if (options.map_starx == 7) {
					hoshi = '★★★★★★★';
					HOSHI_SET.push(hoshi);
				} else if (options.map_starx == 8) {
					hoshi = '★★★★★★★★';
					HOSHI_SET.push(hoshi);
				}

				function Tochi(name, dist, url, alt) {
					this.name = name;
					this.dist = dist;
					this.url = url;
					this.alt = alt;
				}
				function cmp_dist(a, b) {
					return a.dist - b.dist;
				}
				for (i = 0; i < HOSHI_SET.length; ++i) {
					hoshi = HOSHI_SET[i];
					var count = 0;
					var tmp2 = '';
					var Tochis = [];
					$('AREA[onmouseover*=", \'' + hoshi + '\',"]').each(area_mouseover);
					Tochis.sort(cmp_dist);
					for (var j = 0; j < Tochis.length; ++j) {
						if (hoshi == '★★★') {
							hoshi = '★３';
						}
						if (hoshi == '★★★★') {
							hoshi = '★４';
						}
						if (hoshi == '★★★★★') {
							hoshi = '★５';
						}
						if (hoshi == '★★★★★★') {
							hoshi = '★６';
						}
						if (hoshi == '★★★★★★★') {
							hoshi = '★７';
						}
						if (hoshi == '★★★★★★★★') {
							hoshi = '★８';
						}
						var $hosiarea = $('<DIV class="ixamoko_regmap" style="cursor: pointer; padding: 3px 5px; margin: 1px;border-radius:2px;"><span class="star_range">' + hoshi + '</span><span class="coordinate_range">' + Tochis[j].name + '</span> / <span class="star_distance_range">[' + Tochis[j].dist + ']</span></DIV>').attr('url', Tochis[j].url).attr('alt', Tochis[j].alt)
						.hover(mouseover, mouseout).appendTo($status_box);
					}
				}
				function area_mouseover() {
					var $this = $(this);
					var tmp = $this.attr('alt').match(/^([^(]+) (-?\d+),(-?\d+)$/);
					if (tmp === null) {
						//console.log('tmp null3');
						return;
					}
					tmp[0] = '(' + tmp[0].replace('空き地', '') + ')';
					var dist = Math.sqrt(Math.pow(parseInt(tmp[2], 10) - base_x, 2) + Math.pow(parseInt(tmp[3], 10) - base_y, 2));
					dist = Math.floor(dist * 10) / 10;
					if (hoshi == '★★★★★★' || hoshi == '★★★★★★★' || hoshi == '★★★★★★★★') {
						tmp2 = '';
						tmp2 = $this.attr("onmouseover").toString();
						tmp2 = tmp2.replace("function onmouseover(event) {\n", '');
						tmp2 = tmp2.replace('  rewrite(', '');
						tmp2 = tmp2.replace(' ', '');
						tmp2 = tmp2.replace(/'/g, '');
						tmp2 = tmp2.split(',');
						tmp2 = '(' + tmp2[8].replace(' ', '') + tmp2[9].replace(' ', '') + tmp2[10].replace(' ', '') + tmp2[11].replace(' ', '') + tmp2[12].replace(' ', '') + ') ';
						tmp2 = tmp2.replace(/'/g, '');
						tmp[0] = tmp2;
					}
					Tochis[count++] = new Tochi(tmp[0], dist, $this.attr('href'), $this.attr('alt'));
				}
				function mouseover() {
					$(this).css('background-color', '#937824');
				}
				function mouseout() {
					$(this).css('background-color', '');
				}
			}

