			function asort(hash, key) {
				hash.sort(function(b1, b2) {
					return b1[key] > b2[key] ? 1 : -1;
				});
				return hash;
			}

			function dsort(hash, key) {
				hash.sort(function(b1, b2) {
					return b1[key] > b2[key] ? -1 : 1;
				});
				return hash;
			}

