function inspect(obj, max_levels, level, doFunctions){
	var str = '', msg;
	if(level === null)  level = 0;
	if(max_levels === null) max_levels = 1;
	if(max_levels < 1)
		return 'Inspect Error: Levels number must be > 0';
	if(obj === null)
		return 'ERROR: Object is NULL\n';
	var indent = '';
	for (var i=0; i<level; i++)
		indent += '  ';
	for(property in obj) {
		try {
			if (doFunctions===true && $J.isFunction(obj[property])){
				str += indent + '(function) ' + property + '[FUNCTION]\n';
			} 
			else if (!$J.isFunction(obj[property])) {
				str += indent + '(' + ($J.isArray(obj[property])?'array':(typeof obj[property])) + ') ' + property + ( (obj[property]==null)?(': null'):('')) +' = '+ obj[property] +'\n';
			}
			if((typeof obj[property] === 'object' || $J.isArray(obj[property])) && (obj[property] !== null) && (level+1 < max_levels))
			str += inspect(obj[property], max_levels, level+1, doFunctions);  // recurse
		}
		catch(err) {
			// Is there some properties in obj we can't access? Print it red.
			if(typeof(err) === 'string') msg = err;
			else if(err.message)        msg = err.message;
			else if(err.description)    msg = err.description;
			else                        msg = 'Unknown';
			str += '(Error) ' + property + ': ' + msg +'\n';
		}
	}
	str += '\n';
	return str;
}

function sizeObj(obj,level){
	var ret = {entry_number : 0 , total_entry : 0 , size_property_name : 0,  size_value : 0};
	if (!level) level = 0;
	if (obj === null || typeof obj == 'undefined')
		return ret;
	for(var property in obj) {
		if (typeof property == 'undefined') continue;
		ret.entry_number ++;
		ret.total_entry ++;
		ret.size_property_name +=property.length;
		try {
			if((typeof obj[property] === 'object' || $J.isArray(obj[property])) && (obj[property] !== null)) {
				var rec = sizeObj(obj[property],level+1); //recurse
				ret.total_entry += rec.total_entry;
				ret.size_property_name += rec.size_property_name;
				ret.size_value += rec.size_value;
			} else {
				if (!$J.isFunction(obj[property])) {
			 		if (obj[property]!==null && typeof obj[property] !== 'undefined') {
			 			switch (typeof obj[property]) {
			 				case 'number' :
			 					ret.size_value += 8;
			 					break;
			 				case 'boolean' :
			 					ret.size_value += 4;
			 					break;
			 				case 'string' :
			 					ret.size_value += obj[property].length;
			 					break;
			 				default :
			 					console.log('no size defined for '+(typeof obj[property]));
			 					break;
			 			}
			 		}
				}
			}
		}
		catch(err) {
			// Is there some properties in obj we can't access? Print it red.
			if(typeof(err) === 'string') msg = err;
			else if(err.message)        msg = err.message;
			else if(err.description)    msg = err.description;
			else                        msg = 'Unknown';
			console.log( '(Error sizeObj) ' + property + ': ' + msg);
		}
	}
	return ret;
}



