var TIME_FORMAT_DHMS = ['d','h','m','s'];

function timeFormat(time, full,color) {
	time = parseInt (time);
	var t = time;
	var dhms = TIME_FORMAT_DHMS;
	
	var str = '';
	if ( color == undefined ) color = 'tfb'
	
	if (t < 61){
		if ( color == 'NOCOLOR' ) return t + dhms[3];
		return  '<span class='+color+'-s>' + t + dhms[3] + '</span>';
	}
	
	if (t > 86400){
		if ( color == 'NOCOLOR' ) str += (parseInt(t/86400)) + dhms[0];
		else str += '<span class='+color+'-d>' + (parseInt(t/86400)) + dhms[0] + '</span>';
		t %= 86400;
	}  
	if (t>3600 || time>3600){
		if ( color == 'NOCOLOR' ) str += (parseInt(t/3600)) + dhms[1];
		else str += '<span class='+color+'-h>'+ (parseInt(t/3600)) + dhms[1] + '</span>';
		t %= 3600;
	}  
	
	if ( color == 'NOCOLOR' ) str += (parseInt(t/60)) + dhms[2];
	else str += '<span class='+color+'-m>' + (parseInt(t/60)) + dhms[2] + '</span>';
	
	if (full || time<=3600 ){
		if ( color == 'NOCOLOR' ) str += (t%60) + dhms[3];
		else str += '<span class='+color+'-s>' + (t%60) + dhms[3] + '</span>';
	}
	
	return str;
}

