	$('.sms-option').not('.glass, .generation').click(function() {
		sms_classtype = $(this).attr("class").split(/\s/)[1].toString();
		// alert(sms_classtype);
		if($(this).hasClass('disabled')) {
			$(this).removeClass('disabled');
			sms_list[sms_classtype] = 1;
			GM_setValue('option_search_'+sms_classtype, 1);
		} else {
			$(this).addClass('disabled');
			sms_list[sms_classtype] = 0;
			GM_setValue('option_search_'+sms_classtype, 0);
		}
		setAC();
	});
	
	$('.sms-option.generation').click(function() {
		if ($(this).hasClass('gen1') || $(this).hasClass('gen2') || $(this).hasClass('gen3') || $(this).hasClass('gen4') || $(this).hasClass('gen5')) {
			$(this).removeClass('gen'+sms_option['gen']);
			if (sms_option['gen'] < 5) {
				sms_option['gen']++;
			} else {
				sms_option['gen'] = 1;
			}
			GM_setValue('option_search_gen', sms_option['gen']);
			$(this).addClass('gen'+sms_option['gen']);
		} else {
			$(this).attr('class', 'sms-option generation gen5');
		}
		setAC();
	});
	
	// Expand options
	sms_clicks = 0;
	$('.sms-option.glass').click(function() {
		sms_clicks++;
		if (sms_clicks == 1) {
			setTimeout( function() {
				if (sms_clicks == 1) {
					sms_exp_dir = 0;
					if ($('.sms-customize').hasClass('open')) {
						sms_exp_dir = -1;
						$('.sms-customize').animate({'width': '18px'}, function() { sms_exp_dir = 0; });
						$('.sms-customize').removeClass('open');
						GM_setValue('option_search_open', 0);
					} else {
						sms_exp_dir = 1;
						$('.sms-customize').animate({'width': '135px'}, function() { sms_exp_dir = 0; });
						$('.sms-customize').addClass('open');
						GM_setValue('option_search_open', 1);
					}
				} else if (sms_clicks == 2) {
					sms_auto_newnum = prompt("Set max autocomplete entries");
					if (parseInt(sms_auto_newnum) == sms_auto_newnum) {
						$('.sms-option.glass').css('background-color', '#00ff00');
						$('.sms-option.glass').animate({'background-color': '#f5f7fa'});
						sms_option['search_limit'] = sms_auto_newnum - 1;
						GM_setValue('option_search_max', sms_auto_newnum - 1);
					} else {
						$('.sms-option.glass').css('background-color', '#ff0000');
						$('.sms-option.glass').animate({'background-color': '#f5f7fa'});
					}
				}
				sms_clicks = 0;
			}, 250);
		}
	});
	
