	// Adds to autocomplete list
	function setAC() {
		popList();
		$('#search input[name="q"]').autocomplete({
			source: sms_searchlist,
			focus: function() { $('.ui-menu-item').css('cursor', 'pointer'); },
			select: function(event, ui) { gotoOption(0, ui.item.value); },
			open: function() { if (sms_option['search_limit'] > -1) { $('#ui-id-1 .ui-menu-item:gt('+sms_option['search_limit']+')').css('display', 'none'); } }
		});
	}
	setAC();
	
	// Redirect directly there
	$('#search form[action="/search/"]').submit(function(event) {
		event.preventDefault();
		gotoOption(0, $('#search form input[name="q"]').val().toLowerCase().replace(/\b[a-z]/g, function(letter) { return letter.toUpperCase(); }));
	});
	
	genget = ['', 'rb','gs','rs','dp','bw'];
	
	function gotoOption(mode, option) {
		if (mode == -1) {
			window.location.href = "https://encrypted.google.com/search?q="+(option.replace(" ", "+"))+"+site%3Asmogon.com";
		} else {
			if ($.inArray(option, sms_pkmn[sms_option['gen']]) > -1) {
				window.location.href = 'http://www.smogon.com/'+genget[sms_option['gen']]+'/pokemon/'+option.replace(' ', '_').toLowerCase();
			} else if ($.inArray(option, sms_abil[sms_option['gen']]) > -1) {
				window.location.href = 'http://www.smogon.com/'+genget[sms_option['gen']]+'/abilities/'+option.replace(' ', '_').toLowerCase();
			} else if ($.inArray(option, sms_move[sms_option['gen']]) > -1) {
				window.location.href = 'http://www.smogon.com/'+genget[sms_option['gen']]+'/moves/'+option.replace(' ', '_').toLowerCase();
			} else if ($.inArray(option, sms_item[sms_option['gen']]) > -1) {
				window.location.href = 'http://www.smogon.com/'+genget[sms_option['gen']]+'/items/'+option.replace(' ', '_').toLowerCase();
			} else if (($.inArray(option, sms_tier[sms_option['gen']]) > -1) || ($.inArray(option.toUpperCase(), sms_tier[sms_option['gen']]) > -1)) {
				window.location.href = 'http://www.smogon.com/'+genget[sms_option['gen']]+'/tiers/'+option.replace(' ', '_').toLowerCase();
			} else {
				// window.location.href = "https://encrypted.google.com/search?q="+(option.replace(" ", "+"))+"+site%3Asmogon.com";
				window.location.href = "http://www.smogon.com/search/#search="+option.replace(" ", "_").toLowerCase();
			}
		}
	}
	
	function getHashValue(key) {
		return location.hash.match(new RegExp(key+'=([^&]*)'))[1];
	}
	
