// ==UserScript==
// @name          BerryButtons
// @version       1.2
// @namespace     wutMadeScript
// @author        wut
// @description	  Buttons that do things on BerryTube! - for Opera
// @include       http://www.berrytube.tv/*
// @include       http://berrytube.tv/*
// ==/UserScript==

window.onload = function() {    
    var zNode       = document.createElement ('div');

    var browserQuirk = 1;
    var btnWidth = 75;
    var btnHeight = 29; 
    var themeBtnHeight = 11;
    var fontSize = 10;
    var switchHeight = 12;
    var menuWidth = 85;
    var menuHeight = 203;
    
    function getWideButton(id, text){
        return '<button id="'+id+'" type="button" style="width: '+btnWidth+'px; height: '+btnHeight+'px; position: relavite; top: 0px; left: 0px; font-size: 10px; background: orange; border: 2px solid black; margin: 5px 5px 0px 5px;  z-index: 994; padding: 5px 5px;">'+text+'</button>';
    }
    
    function getHalfButton(id, text, position){
        return '<button id="'+id+'" type="button" style="width: '+(((btnWidth-15)/2)+5)+'px; height: '+btnHeight+'px; position: relative; top: 0px; left: 0px; font-size: 10px; background: orange; border: 2px solid black; margin: 5px 5px 0px '
        +( (position == "left") ? ("5") : ( (position == "right") ? ("0") : ("") ) )+
        'px;  z-index: 994; padding: 5px 5px;">'+text+'</button>';
    }
    
    function getScrollButton(id){
        return '<button id="'+id+'" type="button" style="width: '+(menuWidth)+'px; height: '+switchHeight+'px; position: relative; top: '+ (-1-browserQuirk) +'px; left: 0px; font-size: 10px; background: gray; border: 0px outset black; margin: '
        +( (id == "upButton") ? (""+(3+browserQuirk)+"") : ( (id == "downButton") ? ("0") : ("") ) )+
        'px 0px 0px 0px;  z-index: 994; padding: 0px; ">'
        +( (id == "upButton") ? ("^") : ( (id == "downButton") ? ("v") : ("") ) )+
        '</button>'
    }
    
    function getMenuAttr(position){
     return 'width: '+(menuWidth)+'px; height: '+(menuHeight)+'px; position: fixed; top: '
      +( (position == "up") ? ("-500") : ( (position == "down") ? ("0") : ("") ) )+
     'px; left: 0px; font-size: 10px; background: white; border: 1px solid black; margin: 0px;  z-index: 993; padding: 0px;';
    }
    
    function getThemeButton(id, color, position, src){
        return '<button id="'+id+'" class="themePickerBtn" type="button" value="'+src+'" style="width: '+(((btnWidth-45)/5)+5)+'px; height: '+themeBtnHeight+'px; position: relative; top: 0px; left: 0px; font-size: 3px; background: '+color+'; border: 2px solid black; margin: 5px 5px 0px '
        +( (position == "1") ? ("5") : ("0") )+
        'px;  z-index: 994; padding: 0px;"></button>';
    }

    zNode.innerHTML =   getWideButton("chatButton", "Chat Only")+ 
                        getWideButton("videoButton", "Video Only")+
                        getWideButton("clearButton", "Clear Chat")+
                        getHalfButton("squeeButton", "S", "left")+
                        getHalfButton("yayButton", "Y", "right")+
                        getWideButton("settingsButton", "Settings")+
                        getThemeButton("berryTButton", "#942466", "1", "berry")+
                        getThemeButton("lunaTButton", "#1F3570", "2", "luna")+
                        getThemeButton("celestiaTButton", "#FFDE87", "3", "celestia")+
                        getThemeButton("pinkyTButton", "#EF4E91", "4", "pinkie")+
                        getThemeButton("feastTButton", "#CEC27E", "5", "feast")+
                        getScrollButton("upButton");
    
    zNode.setAttribute ('id', 'buttonContainer');
    
    zNode.setAttribute ('style', getMenuAttr("down"));
    document.body.appendChild (zNode);

    var yNode       = document.createElement ('div');
    yNode.innerHTML = getScrollButton("downButton");
    yNode.setAttribute ('id', 'downButtonContainer');
    yNode.setAttribute ('style', 'width: '+(menuWidth)+'px; height: '+(switchHeight)+'px; position: fixed; top: 0px; left: 0px; font-size: 12px; background: white; margin: 0px;  z-index: 991; padding: 0px; border-color: black; border-style: solid; border-width: 1px 1px 1px 1px;');
    document.body.appendChild (yNode);
    
    document.getElementById ("squeeButton").addEventListener ("click", ButtonSquee, false);
    document.getElementById ("yayButton").addEventListener ("click", ButtonYay, false);
    document.getElementById ("chatButton").addEventListener ("click", ButtonChat, false);
    document.getElementById ("videoButton").addEventListener ("click", ButtonVideo, false);
    document.getElementById ("clearButton").addEventListener ("click", ButtonClear, false);
    document.getElementById ("settingsButton").addEventListener ("click", ButtonSettings, false);
    
    
    document.getElementById ("upButton").addEventListener ("click", ButtonUp, false);
    document.getElementById ("downButton").addEventListener ("click", ButtonDown, false);
    
    
    var themes = document.getElementsByClassName ("themePickerBtn");
    
    for(var i = 0; i<themes.length ; i++){
        themes[i].addEventListener ("click", ButtonTheme, false);
    }
    
        
    function ButtonTheme (zEvent)
    {
        var theme = this.value;
        switch(this.value){
            case 'berry':                
                exec(function() {
                    setColorTheme("");
                });
            break;
            case 'luna':                
                exec(function() {
                    setColorTheme("css/colors-woona.css");
                });
            break;
            case 'celestia':                
                exec(function() {
                    setColorTheme("http://backstage.berrytube.tv/malsententia/celestiatube.css");
                });
            break;
            case 'pinkie':                
                exec(function() {
                    setColorTheme("http://backstage.berrytube.tv/malsententia/pinkietube.css");
                });
            break;
            case 'feast':                
                exec(function() {
                    setColorTheme("css/colors-appleoosans.css");
                });
            break;
        }
    }
    
    
    function ButtonUp (zEvent)
    {
        document.getElementById("buttonContainer").setAttribute ('style', getMenuAttr("up"));
    }
    function ButtonDown (zEvent)
    {
        document.getElementById("buttonContainer").setAttribute ('style', getMenuAttr("down"));
    }


    function ButtonSettings (zEvent)
    {
        exec(function() {
            showConfigMenu();
        });
    }
    
    
    function ButtonSquee (zEvent)
    {
        (new Audio("data:audio/ogg;base64,")).play();
    }
    
    function ButtonYay (zEvent){
    (new Audio("data:audio/ogg;base64,")).play();
    }
    
    
    function ButtonChat (zEvent)
    {
        ReffitChat();
        var inn = document.getElementById('videowrap'); inn.parentNode.removeChild(inn);
        exec(function() {
            socket.emit("chatOnly",true);
        });
        window.onresize = function() {
            ReffitChat();
        };
    }
    
    function ReffitChat(){
    
        document.body.style.overflow = 'hidden';
        var inn = document.getElementById('rightpane'); inn.style.cssText = "auto !important; width = "+window.innerWidth+"px; height = "+window.innerHeight+"px; position:fixed; top:0; bottom:0; left:0; right:0; overflow:hidden; z-index:991;"; 
        inn.style.setProperty("width", "100%");
        inn.style.setProperty("height", "100%");

        var inn = document.getElementById('chatpane'); inn.style.cssText = "auto !important; overflow:hidden;"; 
        inn.style.setProperty("width", "100%");
        inn.style.setProperty("height", "100%");

        var inn = document.getElementById('chatbuffer'); 
        inn.style.setProperty("width", window.innerWidth-200+"px");
        inn.style.setProperty("height", window.innerHeight-55+"px");

        var inn = document.getElementById('chatlist'); 
        inn.style.setProperty("width", "200px");
        
        var inn = document.getElementById('connectedCountWrapper'); 
        inn.style.setProperty("width", "199px");
        
        var nicks = document.getElementsByClassName('setNick'); 
        nick = nicks[0];
        if (nick != null){
            var inn = document.getElementById('chatinput'); inn.style.cssText = "auto !important; overflow:hidden;"; 
            inn.style.setProperty("width", window.innerWidth-210+"px");
            
            nick.style.cssText = "auto !important; overflow:hidden; top: "+(window.innerHeight-32)+"px;"; 
        }else{
            var inn = document.getElementById('chatinput'); inn.style.cssText = "auto !important; overflow:hidden;"; 
            inn.style.setProperty("width", window.innerWidth-210+"px");
        }

        var inn = document.getElementById('chatlist');
        inn.style.setProperty("height", window.innerHeight+"px");
    }
    
    function ButtonVideo (zEvent)
    {
        var inn = document.getElementById('videowrap'); inn.style.cssText = "auto !important; width = "+window.innerWidth+"px; height = "+window.innerHeight+"px; position:fixed; top:0; bottom:0; left:0; right:0; overflow:hidden; z-index:990"; 
        inn.style.setProperty("width", "100%");
        inn.style.setProperty("height", "100%");
        var inn = document.getElementById('ytapiplayer'); inn.style.cssText = "width = "+window.innerWidth+"px; height = "+window.innerHeight+"px;auto !important; position:fixed; top:0; bottom:0; left:0; right:0; overflow:hidden; z-index:1";
        inn.style.setProperty("width", "100%");
        inn.style.setProperty("height", "100%");
        document.body.style.overflow = 'hidden';
        var inn = document.getElementById('rightpane'); inn.parentNode.removeChild(inn);
        
    }




    function ButtonClear (zEvent)
    {
        exec(function() {
            $("div[class^=msg]").each(function () {
                if ($("div[class^=msg]").length > 5) {
                    $(this).remove();
                } 
            });
        });
    }

    
    function exec(fn) {
        var script = document.createElement('script');
        script.setAttribute("type", "application/javascript");
        script.textContent = '(' + fn + ')();';
        document.body.appendChild(script);
        document.body.removeChild(script);
    }
    

}
