/*JSON FUNCTIONS*/
function mergeJSON(obj1, obj2)
{   
    var newobj = {};

    for(var z in obj1) {
        newobj[z] = obj1[z];
    }

    for(var z in obj2) {
        newobj[z] = obj2[z];
    }

    return newobj;
}

function sortJSONByValue(obj)
{
    var newobj = {};
    var temp = [];

    for(var key in obj)
    {
        temp.push(obj[key]);
    }

    temp.sort();

    for(var i = 0; i < temp.length; i++)
    {
        for(var key in obj)
        {
            if(obj[key] == temp[i])
            {
                newobj[key] = temp[i];
                break;
            }
        }
    }

    return newobj;
}

function sortJSONByKey(obj)
{
    var newobj = {};
    var temp = [];

    for(var key in obj)
    {
        temp.push(key);
    }

    temp.sort();

    for(var i = 0; i < temp.length; i++)
    {
        for(var key in obj)
        {
            if(key == temp[i])
            {
                newobj[key] = obj[key];
                delete obj[key];
                break;
            }
        }
    }

    return newobj;
}

function reverseJSON(obj)
{   
    var newobj = {};

    for(var key in obj)
    {
        newobj[obj[key]] = key;
    }

    return newobj;    
}

function getNestedProperties(obj)
{   
    var newobj = {};
    var current;

    for(var x in obj)
    {
        current = obj[x];
        if(typeof current === "object")
        {
            var subproperties = getNestedProperties(current);

            for(var y in subproperties)
            {
                newobj[y] = subproperties[y];
            }
        }
        else if(obj.hasOwnProperty(x))
        {
            newobj[x] = current;
        }
    }
    return newobj;
}   

