// ==UserScript==
// @name           Newgrounds Fossil
// @namespace      ngfossil@snakehole.net
// @include        http://www.newgrounds.com/bbs/forum/*
// @include        http://www.newgrounds.com/bbs/topic/*
// @description Marks old threads and posts on Newgrounds
// ==/UserScript==
var ngOTIoldImage = 'url(data:image/png;base64,)';
var ngOTIpostStar = 'url(data:image/png;base64,)';
var ngOTIpostStar_mid = 'url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAC0AAAABCAYAAABKdCfXAAAAAXNSR0IArs4c6QAAAAZiS0dEAP8A/wD/oL2nkwAAAAlwSFlzAAALEwAACxMBAJqcGAAAAAd0SU1FB9wDCgMiMAIYJJUAAAAeSURBVAjXYzy5b58UAwMDg7mT0zNkNsMAAEL2w+QB6Q0OYZIcnCYAAAAASUVORK5CYII=)';
var ngOTIpostStar_bot = 'url(data:image/png;base64,)';
var ngOTI = {
	css: null,
	addCSS: function (styles) {
    	var style = document.createElement("style");
    	style.type = "text/css";
    	style.textContent = styles.toString();
    	document.body.appendChild(style);
	},
	compareDates: function(date) {
		var d1=new Date();
		var d2=new Date(date);
		// Get 1 day in milliseconds
		var one_day=1000*60*60*24
		if((d1 - d2) / (one_day) >= 75) //if more than 75 days, it's an old thread
		{
			return true;
		}
	},
	checkForOldThreads: function ()	{
		var threads = document.querySelector('table.forumlist tbody').getElementsByTagName('tr');
		for (var i = threads.length - 1; i >= 0; i--) {
			if(!threads[i].querySelector('td+td+td a span').innerHTML.match('ago'))
			{
				if(ngOTI.compareDates(threads[i].querySelector('td+td+td a span').textContent)) //if more than 90 days, it's an old thread
				{
					threads[i].className += " oldThreadInd";
				}
			}
		};
	},
	checkForOldPosts: function ()	{
		var posts = document.querySelectorAll('div[id^=bbspost]');
		for (var i = posts.length - 1; i >= 0; i--) {
			//GM_log(posts[i].querySelector('.post-bbs .bbstext .textblock div').textContent)
			//GM_log(posts[i].querySelector('.post-bbs .bbstext .textblock div').childNodes[2].textContent.match(/Posted ([\s\d\w\.\,]*)/)[1].replace(/([\.\,])|(rd)|(st)|(th)/gi,''));
			if(ngOTI.compareDates(posts[i].querySelector('.post-bbs .bbstext .textblock div').childNodes[2].textContent.match(/Posted ([\s\d\w\.\,]*)/)[1].replace(/([\.\,])|(rd)|(st)|(th)|(nd)/gi,'')))
			{
				posts[i].className += " oldPostInd";
			}
		};
	},
	init: function() {
		ngOTI.css = 'table.forumlist tr.oldThreadInd td:first-child a {background-image:' + ngOTIoldImage + '} .oldPostInd div.post-bbs > div:first-child {background-image:' + ngOTIpostStar + '} .oldPostInd div.post-bbs > div:first-child > span:first-child {background-image:' + ngOTIpostStar_mid + '} .oldPostInd div.post-bbs > div:first-child > span:first-child > span {background-image:' + ngOTIpostStar_bot + '}';
		ngOTI.addCSS(ngOTI.css);
		if(document.querySelector('table.forumlist tbody')){ngOTI.checkForOldThreads()} else {ngOTI.checkForOldPosts()};
	}
};
ngOTI.init();