// ==UserScript==
// @name       Youtube comment notifications
// @namespace  http://www.twitter.com/littlejennaisme
// @version    1.0b
// @description  The notifications API currently only works on google chrome. Doesn't work on https. Sorry. The script fetches comment data from google API using JSON. The interval of which it checks for new comments is currently locked on 120 seconds, it also doesn't do any complex searches on comments each check. In other words. If two new comments appeared during that 2 minute time span, only the newest one will show up. All of this to be fixed in a later version, perhaps. There's also some bug i haven't quite figured out yet which makes it so that the same comment will appear randomly. Probably something in the content data?
// @match      http://www.youtube.com/all_comments*
// @copyright  2012+, Jenna
// ==/UserScript==

var CheckData = null;


var Permission = document.createElement('div');
Permission.setAttribute("id", "PermissionButton");

document.getElementById("threaded-option").appendChild(Permission);
document.getElementById("PermissionButton").innerHTML = "<span style=\"cursor: pointer; color: #1C62B9;\">Enable desktop notifications (If they're not enabled yet)(beta)(Chrome only)</span>";

function createNotificationInstance(options, sImg, sNote, sContent)
{
    if(options.notificationType == 'simple')
    {
        return window.webkitNotifications.createNotification(sImg, sNote, sContent);
    }
    else if(options.notificationType == 'html')
    {
        return window.webkitNotifications.createHTMLNotification('http://i.imgur.com/emo98.png');
    }
}

setInterval(function(){

    if(window.webkitNotifications.checkPermission() == 0)
    {
        $.getJSON('http://gdata.youtube.com/feeds/api/videos/' + window.location.href.substr(38, 11) + '/comments?alt=json', function(data) {
                    
                    /*if(CheckData == null)
                    {
                        CheckData = data.feed.entry[0].content.$t;
                    }*/
                    //alert(CheckData);
                        
                    if(CheckData != data.feed.entry[0].content.$t || CheckData == null)
                    {
                        
                        var CONTENT = data.feed.entry[0].content.$t;
                        var TITLE = data.feed.entry[0].author[0].name.$t + " - '" + $("#comment-video-info .vtitle span a").html().substr(0, 20) + "...'";
                        
                        var Noti = createNotificationInstance({ notificationType: 'simple' }, "data:image/jpeg;base64,", TITLE, CONTENT);
					Noti.ondisplay = function() {};
					Noti.onclose = function() {};
					Noti.show();
                    
                        
                    CheckData = data.feed.entry[0].content.$t;
                    }
                    
                    });
    }
    else
    {
        window.webkitNotifications.requestPermission();
    }

},60000*2);


