// ==UserScript==
// @id             org.userscripts.users.kuehlschrank.ImdbIconBar
// @name           IMDb Icon Bar
// @version        2013.2.6
// @description    Links to search for subtitles, torrents, NZBs and more
// @author         kuehlschrank
// @homepage       http://userscripts.org/users/205817
// @include        http://*.imdb.tld/title/*/
// @include        http://*.imdb.tld/title/*/#*
// @include        http://*.imdb.tld/title/*/reference*
// @include        http://*.imdb.tld/title/*/combined*
// @include        http://*.imdb.tld/title/*/maindetails*
// @include        http://*.imdb.tld/title/*/fullcredits*
// @noframes
// ==/UserScript==

(function() {

	'use strict';
	
	// Possible placeholders in URL: %id, %title and %title[padding] (e.g. %title[_])
	// Inserting null creates a seperator.
	// Favicon converter: http://dataurl.net/#dataurlmaker

	var sites = [
		{name:'OpenSubtitles', url:'http://www.opensubtitles.org/en/search/imdbid-%id/sublanguageid-eng', icon:'data:;base64,'},
		{name:'Subscene', url:'http://subscene.com/filmsearch.aspx?q=%title&exact=on', icon:'data:image/png;base64,%3D'},
		{name:'Podnapisi', url:'http://www.podnapisi.net/de/ppodnapisi/search?sK=%title', icon:'data:image/x-icon;base64,'},
		{name:'Zarban’s House of Commentaries', url:'http://www.zarban.com/?s=%title', icon:'data:image/x-icon;base64,'},
		{name:'letterboxd', url:'http://letterboxd.com/imdb/tt%id', icon:'data:image/png;base64,'},
		{name:'Box Office Mojo', url:'http://boxofficemojo.com/search/?q=%title', icon:'data:image/png;base64,'},
		{name:'YouTube', url:'http://www.youtube.com/results?search_query=%22%title%22%20trailer', icon:'data:image/x-icon;base64,%3D%3D'},
		{name:'Wikipedia', url:'http://en.wikipedia.org/wiki/Special:Search?search=%title&go=Go', icon:'data:image/x-icon;base64,'},
		{name:'PreDB', url:'http://predb.me/?search=%title&cats=movies', icon:'data:image/x-icon;base64,'},
		{name:'NZBIndex', url:'http://www.nzbindex.nl/search/?q=%title', icon:'data:image/x-icon;base64,'},
		{name:'movies.io', url:'http://movies.io/m/search?q=%title', icon:'data:;base64,'},
		{name:'The Pirate Bay', url:'http://thepiratebay.se/search/%title/0/99/200', icon:'data:image/x-icon;base64,'},
		{name:'Google Blog Search', url:'https://www.google.com/search?q=intitle%3A%22%title%22%20720p&tbm=blg', icon:'data:image/x-icon;base64,'}
	];

	var title = document.title.match(/^(.*) \(/)[1];
	var id    = location.pathname.match(/\/tt(\d+)\//)[1];

	function html() {
		var h = '';
		for(var i = 0, len = sites.length; i < len; i++) {
			var s = sites[i];
			h += s ? '<span><a href="' + url(s) + '"><img src="' + s.icon + '" title="' + s.name + '"><\/a>&nbsp;</span>' : '&nbsp;&nbsp;';
		}
		return h;
	}

	function url(site) {
		var u = site.url.replace(/%id/, id);
		if(u.match(/%title\[(.*?)\]/)) {
			return u.replace(/%title\[.*?\]/, encodeURIComponent(title.replace(/ /g, RegExp.$1)));
		} else {
			return u.replace(/%title/, encodeURIComponent(title));
		}
	}

	var bar = document.createElement('div');
	bar.style.cssText = 'position:absolute; top:-20px; right:0; text-align:right;';
	bar.innerHTML = html();
	var h1 = document.querySelector('h1');
	h1.style.position = 'relative';
	h1.appendChild(bar);

})();