    function doNextLevel (){
      while ( curLevel<10 && cList['lvl'+ ++curLevel].length==0)
        ;
      if (curLevel>=10){
        endBulkAdd ('done!<BR>');
        return;
      }
     e_attackDialog(false);
    }
        
