// ---------------------------------------------------------------------------------------------------------------------

// -- Constants --------------------------------------------------------------------------------------------------------
var VERSION = '1.1.3.4';
var RELEASE_DAY = '25. April 2011.';

var eSidebarOptions = {position: 'Left', height: '90', firsttime: true, state: false};
var eSidebarData = {sidemenu1: {title: '', lnk: '', target: false}};
// ---------------------------------------------------------------------------------------------------------------------

// -- Common functions -------------------------------------------------------------------------------------------------
function readSettings(name) {
	if (name == 'settings') {
		var settingString = GM_getValue('Options');
		if (settingString != null) {
			var options = eval('(' + settingString + ')');
			for(var op in options) {
				eSidebarOptions[op] = options[op];
			}
		} else {
			eSidebarOptions.position = 'Left';
			eSidebarOptions.height = '90';
			eSidebarOptions.firsttime = true;
			eSidebarOptions.state = false;
		}
	} else if (name == 'sidemenu') {
		var settingString = GM_getValue('Data');
		if (settingString != null) {
			var options = eval('(' + settingString + ')');
			for(var op in options) {
				eSidebarData[op] = options[op];
			}
		} else {
			eSidebarData.sidemenu1.title = '';
			eSidebarData.sidemenu1.lnk = '';
			eSidebarData.sidemenu1.target = false;
		}
	}
}

function writeSettings(pref, value) {
	GM_setValue(pref, $.toJSON(value));
}
// ---------------------------------------------------------------------------------------------------------------------

// -- Custom side menu -------------------------------------------------------------------------------------------------
function createSideMenu() {
	readSettings('settings');
	readSettings('sidemenu');
	
	var windowPercent = parseFloat(eSidebarOptions['height']) * 0.01;
	if ($(window).height() * windowPercent >= 80) {
		var windowHeight = $(window).height() * windowPercent - 30;
	} else {
		var windowHeight = 50;
	}
	var buttonCount = Math.floor(windowHeight / 25) - 1;
	windowHeight = (buttonCount + 1) * 25 + 30;
	
	$('body').append('<div id="sideMain" class="sideMain' + eSidebarOptions['position'] + '"><div id="redArrow" class="redArrow"></div><a id="sideOptionsHolder" class="sideOptionsHolder' + eSidebarOptions['position'] + '" title="eSidebar options"></a><div id="sideMainGrip" class="sideMainGrip' + eSidebarOptions['position'] + '"></div><div id="sideContentHolder" class="sideContentHolder' + eSidebarOptions['position'] + '"></div></div>');
	
	if(eSidebarOptions['firsttime'] == true) {
		$('#redArrow').css('display', 'block');
		$('#redArrow').css('height', windowHeight + 'px');
		$('#redArrow').css('top', (100 - windowPercent * 100) / 2 + '%');
	}
	
	$('#sideMain').css('height', windowHeight + 'px');
	$('#sideMain').css('top', (100 - windowPercent * 100) / 2 + '%');
	$('#sideContentHolder').css('height', (buttonCount * 25) + 'px');
	
	for (var i = 1; i <= buttonCount; i++) {
		$('#sideContentHolder').append('<div id="sideButtonHolder_' + i + '" class="sideButtonHolder' + eSidebarOptions['position'] + '"></div>');
		
		if (eSidebarData['sidemenu' + i] == undefined) {
			var sidemenuTitle = '';
			var sidemenuLink = '';
			var sidemenuTarget = false;
			eSidebarData['sidemenu' + i] = {title: sidemenuTitle, lnk: sidemenuLink, target: sidemenuTarget};
			
			writeSettings('Data', eSidebarData);
		}
		
		if (eSidebarData['sidemenu' + i].title != null && eSidebarData['sidemenu' + i].title.length > 1 && eSidebarData['sidemenu' + i].lnk != null && eSidebarData['sidemenu' + i].lnk.length > 1) {
			if (eSidebarData['sidemenu' + i].target == true) {
				$('#sideButtonHolder_' + i).html('<a id="sideOption_' + i + '" class="sideOption' + eSidebarOptions['position'] + '"></a><a id="sideLink_' + i + '" href="' + eSidebarData['sidemenu' + i].lnk + '" target="_blank"><div class="sideButton' + eSidebarOptions['position'] + '">' + eSidebarData['sidemenu' + i].title + '</div></a>');
			} else {
				$('#sideButtonHolder_' + i).html('<a id="sideOption_' + i + '" class="sideOption' + eSidebarOptions['position'] + '"></a><a id="sideLink_' + i + '" href="' + eSidebarData['sidemenu' + i].lnk + '" target="_self"><div class="sideButton' + eSidebarOptions['position'] + '">' + eSidebarData['sidemenu' + i].title + '</div></a>');
			}
		} else {
			$('#sideButtonHolder_' + i).html('<a id="sideOption_' + i + '" class="sideOption' + eSidebarOptions['position'] + '"></a><a id="addSideLinkHere_' + i + '" title="Add current page to this button" href="javascript:;" target="_self"><div class="sideButtonAdd' + eSidebarOptions['position'] + '">+</div></a>');
		}
	}
	
	if(eSidebarOptions['state'] == true) {
		$('#sideMainGrip').css('top', '-25px');
		$('#sideContentHolder').css('display', 'block');
		$('#sideOptionsHolder').css('display', 'block');
	}
	
	$('#sideMainGrip').live('click', function() {
		if(eSidebarOptions['firsttime'] == true) {
			$('#redArrow').css('display', 'none');
			eSidebarOptions['firsttime'] = false;
			writeSettings('Options', eSidebarOptions);
		}
		if ($('#sideContentHolder').css('display') != 'block') {
			$('#sideMainGrip').css('top', '-25px');
			$('#sideContentHolder').css('display', 'block');
			$('#sideOptionsHolder').css('display', 'block');
		} else {
			$('#sideMainGrip').css('top', '0px');
			$('#sideContentHolder').css('display', 'none');
			$('#sideOptionsHolder').css('display', 'none');
		}
	});
	
	liveFunctions();
}

function liveFunctions() {
	readSettings('settings');
	readSettings('sidemenu');
	
	$('#sideOptionsHolder').live('click', function(e) {
		e.stopPropagation();
		
		if($('#optionsMain').length == 0) {
			$('body').append('<div id="optionsMain" class="optionsMain">' +
								'<div class="optionsHeader">' +
									'<a id="optionsClose" class="closeButton" title="Close" href="javascript:;">&nbsp;</a>' +
								'</div>' +
								'<div class="optionsContent">' +
									'<div id="optionsPosHolder" class="optionsItem">' +
										'<div class="optionsItemLabel">Position </div>' +
										'<div style="float: left;">' +
											'<label><input name="positionSelect" type="radio" id="optionsPositionLeft" value="Left" style="vertical-align: text-bottom;"> Left</label><br />' +
											'<label><input name="positionSelect" type="radio" id="optionsPositionRight" value="Right" style="vertical-align: text-bottom;"> Right</label><br />' +
										'</div>' +
									'</div>' +
									'<div class="optionsItem">' +
										'<div class="optionsItemLabel">Height </div>' +
										'<div style="float: left; line-height: 32px;">' +
											'<label><input type="text" id="heightInput" value="' + eSidebarOptions['height'] + '" class="inputBox"> %</label><br />' +
										'</div>' +
									'</div>' +
									'<div class="optionsItem">' +
										'<div class="optionsItemLabel">Keep open </div>' +
										'<div style="float: left; line-height: 32px;">' +
											'<label><input type="checkbox" id="optionsState" style="vertical-align: text-top;"></label><br />' +
										'</div>' +
									'</div>' +
								'</div>' +
								'<div class="optionsVersion">v' + VERSION + '</div>' +
								'<div class="optionsFooter">Note: After you change these settings reload current page in order for settings to take effect.</div>' +
							'</div>');
			
			$('#optionsMain').css('display', 'block');
			
			$('#optionsClose').live('click', function(e) {
				e.stopPropagation();
				$('#optionsMain').remove();
			});
			
			$('#heightInput').live('keyup', function() {
				if (isNaN($(this).val())) {
					$(this).val(eSidebarOptions['height'])
				}
				
				var settHeight = $(this).val();
				if (settHeight > 100) {
					settHeight = 100;
				}
				
				eSidebarOptions['height'] = settHeight;
				writeSettings('Options', eSidebarOptions);
			});
			
			$('#optionsPosHolder input[id^="optionsPosition' + eSidebarOptions['position'] + '"]').attr('checked', 'checked');
	
			$('#optionsPosHolder input').change(function() {
				eSidebarOptions['position'] = $(this).val();
				writeSettings('Options', eSidebarOptions);
			});
			
			$('#optionsState').attr('checked', eSidebarOptions['state']);
			
			$('#optionsState').change(function() {
				eSidebarOptions['state'] = $(this).attr('checked');
				writeSettings('Options', eSidebarOptions);
			});
		}
	});
	
	$('a[id*="sideOption_"], a[id*="addSideLinkHere_"]').live('click', function(e) {
		e.stopPropagation();
		var buttonId = $(this).attr('id').split('_')[1];
		
		if($('.optionPrompt').length == 0) {
			if ($(this).attr('id').split('_')[0] == 'addSideLinkHere') {
				var thisLink = document.location.href;
				var thisName = '';
				var thisTarget = false;
			} else {
				var thisLink = eSidebarData['sidemenu' + buttonId].lnk;
				var thisName = eSidebarData['sidemenu' + buttonId].title;
				var thisTarget = eSidebarData['sidemenu' + buttonId].target;
			}
				
			$('body').append('<div id="optionPrompt_' + buttonId + '" class="optionPrompt">' +
								'<div class="optionHeader">' +
									'<a id="optionClose_' + buttonId + '" class="closeButton" title="Close" href="javascript:;">&nbsp;</a>' +
								'</div>' +
								'<div style="float: left; margin-top: 10px; margin-left: 10px; margin-right: 10px; width: 380px;">' +
									'<div class="optionContentHolder">' +
										'<div class="optionContentLabel">Link</div>' +
										'<div class="optionContent"><input id="optionPromptLink_' + buttonId + '" type="text" size="50" value="' + thisLink + '" class="inputBox"></div>' +
										'<div class="optionContentLabel">Title</div>' +
										'<div class="optionContent"><input id="optionPromptName_' + buttonId + '" type="text" size="50" value="' + thisName + '" class="inputBox"></div>' +
										'<div class="optionContentLabel">New tab</div>' +
										'<div class="optionContent"><input id="optionPromptTarget_' + buttonId + '" type="checkbox" title="Open link in new tab?"></div>' +
									'</div>' +
								'</div>' +
								'<div class="optionFooter">Note: After you change these settings reload current page in order for settings to take effect.</div>' +
							'</div>');
			
			$('#optionPromptTarget_' + buttonId).attr('checked', thisTarget);
			$('#optionPrompt_' + buttonId).css('display', 'block');
			
			$('#optionClose_' + buttonId).live('click', function(e) {
				var buttonId = $(this).attr('id').split('_')[1];
				$('#optionPrompt_' + buttonId).remove();
			});
			
			$('.optionPrompt input').live('keyup', function() {
				var buttonId = $(this).attr('id').split('_')[1];
				
				var linkValue = $('input[id*="optionPromptLink"]').val();
				var nameValue = $('input[id*="optionPromptName"]').val();
				var targetValue = $('input[id*="optionPromptName"]').attr('checked');
				
				if (linkValue != null && linkValue != '' && nameValue != null && nameValue != '') {
					eSidebarData['sidemenu' + buttonId] = {title: nameValue, lnk: linkValue, target: targetValue};
					writeSettings('Data', eSidebarData);
				} else {
					eSidebarData['sidemenu' + buttonId] = {title: '', lnk: '', target: false};
					writeSettings('Data', eSidebarData);
				}
			});
			
			$('.optionPrompt input[id*="optionPromptTarget"]').change(function() {
				var buttonId = $(this).attr('id').split('_')[1];
				
				var linkValue = $('input[id*="optionPromptLink"]').val();
				var nameValue = $('input[id*="optionPromptName"]').val();
				var targetValue = $(this).attr('checked');
				
				if (linkValue != null && linkValue != '' && nameValue != null && nameValue != '') {
					eSidebarData['sidemenu' + buttonId] = {title: nameValue, lnk: linkValue, target: targetValue};
					writeSettings('Data', eSidebarData);
				} else {
					eSidebarData['sidemenu' + buttonId] = {title: '', lnk: '', target: false};
					writeSettings('Data', eSidebarData);
				}
			});
		}
	});
}
// ---------------------------------------------------------------------------------------------------------------------

function Main() {
    if (typeof unsafeWindow == 'undefined') {
        unsafeWindow = window;
	}

	createSideMenu()
};