// ==UserScript==
// @name           LoU Assist
// @description    Adds various functionalities to Lord of Ultima
// @namespace      LoU Assist
// @include        http://prodgame*.lordofultima.com/*/index.aspx*
// @version        1.0.0
// ==/UserScript==

(function()
{
try
{

var LAMain = function() 
{
	function createLA() 
	{
		
		qx.Class.define("LoUAssist.main", 
		{
			type: "singleton",
			extend: qx.core.Object,

			members: 
			{
				app: null,
				player: null,
				btnXEP: null,
				lowerBar: null,
				panelXEP: null,

				initialize: function() 
				{
					this.app = qx.core.Init.getApplication();
					this.player = webfrontend.data.Player.getInstance();
					this.lowerBar = this.app.serverBar;
					
					this.btnXEP = new qx.ui.form.ToggleButton("XEP");
					this.btnXEP.addListener("changeValue", this.showXEPPage, this);
					
					this.panelXEP = new window.LoUAssist.XEPPanel();

					this.tweakLA();
				},

				tweakLA: function() 
				{
                    console.log("Tweak start");
                    this.lowerBar.add(this.btnXEP, {top: 2, left: this.lowerBar.getBounds().width-300});

				},
				
				showXEPPage: function()
				{
				    if(this.btnXEP.getValue() == true)
				    {
				        var btnLeft = this.btnXEP.getBounds().left;
				        var lowerBarTop = this.lowerBar.getBounds().top;
				        var cityBarWidth = this.app.cityBar.getBounds().width;
				        var titleHeight = this.app.title.getBounds().height;
				        this.app.desktop.add(this.panelXEP.panelContainer, {left: btnLeft + cityBarWidth, top: lowerBarTop +titleHeight - 200});
				    }
				    else
				        this.app.desktop.remove(this.panelXEP.panelContainer);
				}
			}
		});
		
		qx.Class.define("LoUAssist.XEPPanel",
		{
		    extend: qx.core.Object,
		    construct: function()
		    {
		        this.panelContainer = new qx.ui.container.Composite(new qx.ui.layout.Canvas());
				this.panelContainerBgr = new qx.ui.basic.Image("data:image/png;base64,");
				this.panelContainerBgr.setWidth(300);
				this.panelContainerBgr.setHeight(140);
				this.panelContainer.add(this.panelContainerBgr, {left: 0, top: 0});
				
		    },
		    members:
		    {
		        panelContainer: null,
		        panelContainerBgr: null
		    }
		
		});
	}
	
	function LACheckIfLoaded() 
	{
		try 
		{
			if (typeof qx != 'undefined') 
			{
				a = qx.core.Init.getApplication(); // application
				c = a.cityInfoView;
				ch = a.chat;
				wdst = webfrontend.data.ServerTime.getInstance().refTime;
				if (a && c && ch && wdst) 
				{
					createLA();
					window.LoUAssist.main.getInstance().initialize();
				} 
				else
					window.setTimeout(LACheckIfLoaded, 1000);
			} 
			else 
			{
				window.setTimeout(LACheckIfLoaded, 1000);
			}
		} 
		catch (e) 
		{
		    console.log(e);
		    console.log(e.stack);
		}
	}
	if (/lordofultima\.com/i.test(document.domain))
		window.setTimeout(LACheckIfLoaded, 1000);
			
}

	// injecting, because there seem to be problems when creating game interface with unsafeWindow
	var LoUAssistScript = document.createElement("script");
		txt = LAMain.toString();
		if (window.opera != undefined)
			txt = txt.replace(/</g,"&lt;"); // rofl Opera
		LoUAssistScript.innerHTML = "(" + txt + ")();";
		LoUAssistScript.type = "text/javascript";
	if (/lordofultima\.com/i.test(document.domain))
		document.getElementsByTagName("head")[0].appendChild(LoUAssistScript);


}
catch(e)
{
    console.log(e);
    console.log(e.stack);
}	
})();