// ==UserScript==
// @name vkontakte smile
// @include http://*vkontakte.ru/*
// @include http://*vk.com/*
// @version 1.0
// ==/UserScript==

(function () {

smileys = [
    ['smile', /:\)/gi, 'Улыбка', ':)', ''],
    ['sadsmile', /:\(/gi, 'Грущу', ':(', ''],
    ['bigsmile', /:D/gi, 'Смеюсь', ':D', ''],
    ['cool', /8-\)/gi, 'Крутой', '8-)', ''],
    ['surprised', /:o/gi, 'Удивляюсь', ':o', ''],
    ['wink', /;\)/gi, 'Подмигиваю', ';)', ''],
    ['crying', /;\(/gi, 'Плачу', ';(', ''],
    ['kiss', /:\*/gi, 'Поцелуй', ':*', ''],
    ['tongueout', /:p/gi, 'Ухмыляюсь', ':p', ''],
    ['blush', /:\$/gi, 'Краснею', ':$', ''],
    ['angry', /:\@/gi, 'Злой', ':@', ''],
    ['giggle', /\(chuckle\)/gi, 'Хихи', '(chuckle)', ''],
    ['evilgrin', /\(eg\)/gi, 'Злобный', '(eg)', ''],
    ['rofl', /\(rofl\)/gi, 'Очень смешно', '(rofl)', ''],
    ['handshake', /\(handshake\)/gi, 'Рукопожатие', '(handshake)', ''],
    ['yes', /\(y\)/gi, 'Да', '(y)', ''],
    ['no', /\(n\)/gi, 'Нет', '(n)', ''],
    ['party', /\(party\)/gi, 'Праздник', '(party)', ''],
    ['happy', /\(happy\)/gi, 'Счастье', '(happy)', ''],
    ['fubar', /\(fubar\)/gi, 'Глупый', '(fubar)', ''],
    ['brokenheart', /\(h\)/gi, 'Разбитое сердце', '(h)', ''],
    ['flower', /\(f\)/gi, 'Цветок', '(f)', ''],
    ['cake', /\(\^\)/gi, 'Торт', '(^)', ''],
    ['cash', /\(cash\)/gi, 'Деньги', '(cash)', ''],
    ['mail', /\(e\)/gi, 'Почта', '(e)', ''],
    ['time', /\(o\)/gi, 'Часы', '(o)', '']
];

function xform(s) { 
    s = s.replace(/&/g, "&amp;")
    .replace(/</g, "&lt;")
    .replace(/>/g, "&gt;")
    for (var i = 0; i < smileys.length; i++) {
        s = s.replace(smileys[i][1], '<img style=" padding: 1px;" src="data:image/gif;base64,' + smileys[i][4] + '" title="' + smileys[i][2] +'"/>')
    }
    return s;
}

function smilize_node(text_node, p) {
    var parent = p;
    if (!p) parent = text_node.parentNode;

    var s = text_node.data;
    if (!s || s.match(/\.write/)) return;

    //try {
        var new_node = document.createElement("span");
        var new_content = xform(s);
        if (new_content != s) {
            new_node.innerHTML = new_content;
            parent.replaceChild(new_node, text_node);
        }
    //} catch(e) {}
}

if (document.evaluate) { 
    var textnodes = document.evaluate( "//table//text()", document, null, XPathResult.UNORDERED_NODE_SNAPSHOT_TYPE, null); 
    for (var i = 0; i < textnodes.snapshotLength; i++) { 
        var node = textnodes.snapshotItem(i); 
        smilize_node(node, null);
    }
}
else {
    function processNode(node, parent) {
        if (node.nodeType == 3) {
            smilize_node(node, parent);
        } else if (node.nodeType == 1) {
            for (var i = 0; i < node.childNodes.length; i++) {
                processNode(node.childNodes[i], node);
            }
        }
    }
    processNode(document.body, document.body);
}

function emoticon(code, nm) {
    if (code != "") {
        var txtarea=document.getElementById(nm);code = ' ' + code + ' ';
    
        if (document.selection) {
            txtarea.focus();
            var txtContent = txtarea.value;
            var str = document.selection.createRange();
            if (str.text == "") {str.text = code;} 
            else if (txtContent.indexOf(str.text) != -1){
                str.text = code + str.text;
            } else {
                txtarea.value = txtContent + code;
            }
        } else{
            txtarea.value = txtarea.value + code;
        }
    }
}


var typeofpage = {"none": 0, "wall" : 1, "newmessage": 2, "showmessage": 3};
var elementname = ["", "br", "postMessage", "postMessage"];
var txtareaname = ["", "reply_field", "messageBody", "reply_field"];
var page = typeofpage.none;

var elBr = document.getElementById(elementname[typeofpage.wall]);
if (elBr && elBr.className == 'ta_r') {
//if (window.location.href.match(/vkontakte\.ru\/id\d+/)) { // Стена
    page = typeofpage.wall;
}
else if (window.location.href.match(/vkontakte\.ru\/mail\.php\?act=write/)) { // Новое сообщение
    page = typeofpage.newmessage;
}
else if (window.location.href.match(/vkontakte\.ru\/mail\.php\?act=show/)) { // Ответ на сообщение
    page = typeofpage.showmessage;
}

if (page != typeofpage.none) {
    var scriptTagCode = document.createElement("script");
    scriptTagCode.setAttribute("type", "text/javascript");
    scriptTagCode.text =
        'function emoticon(code, nm) {                                           '+
        '   if (code != "") {                                                    '+
        '       var txtarea=document.getElementById(nm);code = " " + code + " "; '+
        '       if (document.selection) {                                        '+
        '           txtarea.focus();                                             '+
        '           var txtContent = txtarea.value;                              '+
        '           var str = document.selection.createRange();                  '+
        '           if (str.text == "") { str.text = code; }                     '+
        '           else if (txtContent.indexOf(str.text) != -1) {               '+
        '               str.text = code + str.text;                              '+
        '           } else {                                                     '+
        '               txtarea.value = txtContent + code;                       '+
        '           }                                                            '+
        '       } else {                                                         '+
        '           txtarea.value = txtarea.value + code;                        '+
        '       }                                                                '+
        '   }                                                                    '+
        '}';
    document.getElementsByTagName("head")[0].appendChild(scriptTagCode);

    var cont = document.createElement("div");
    if (page == typeofpage.wall) cont.setAttribute("style", "float:left;");
    else cont.setAttribute("align", "center");

    for (var i = 0; i < smileys.length; i++) {
        var smileImg = document.createElement("img");
        smileImg.setAttribute("style", "cursor: pointer; margin:0;padding:1px;border:0;");
        //smileImg.setAttribute("src", "http://smilebar.ru/_sm/" + smileys[i][0] + ".gif");
        smileImg.setAttribute("src", "data:image/gif;base64," + smileys[i][4]);
        smileImg.setAttribute("title", smileys[i][2]);
        smileImg.setAttribute("onclick", "emoticon('" + smileys[i][3] + "', '" + txtareaname[page] + "'); return false;"
        );
        cont.appendChild(smileImg);

        if (2*(i+1) == smileys.length)
            cont.innerHTML += "<br>";
    }
    document.getElementById(elementname[page]).appendChild(cont);
}

})();