				Shortcut_Icons.addFooterIcon('javascript:if(Character.home_town != null) AjaxWindow.show(\'fingerboard\',{town_id:Character.home_town.town_id},Character.home_town.town_id);','footer_fingerboard','<b>'+values["fingerboard"]+'</b>');
