var Shortcut_Icons_Functions = ['addFooterIcon', 'setToolTip', 'addGlobalStyle', 'setOpacity', 'Show']; 
var Shortcut_Icons_Script = document.createElement('script');
Shortcut_Icons_Script.type='text/javascript';
Shortcut_Icons_Script.text =  'if(window.Shortcut_Icons == undefined) {\n';
Shortcut_Icons_Script.text += '  window.Shortcut_Icons = new Object();\n';
for (var i = 0; i< Shortcut_Icons_Functions.length; i++) {
	var siFunction = Shortcut_Icons_Functions[i];
	Shortcut_Icons_Script.text += '  Shortcut_Icons.' + siFunction + ' = ' + eval(siFunction.toString()) + '\n';
};
Shortcut_Icons_Script.text += '  window.addEvent(\'domready\',Shortcut_Icons.Show);\n';
Shortcut_Icons_Script.text += '}';
document.body.appendChild(Shortcut_Icons_Script);