		var u10 = FarmOptions.Troops[10];
		var u11 = FarmOptions.Troops[11];
		var u12 = FarmOptions.Troops[12];
		var now = new Date().getTime()/1000.0;
		now = now.toFixed(0);
		citynumber = Seed.cities[city-1][0];
		cityID = 'city' + citynumber;
