var gmail = null;
 
window.addEventListener('load', function() {
    if (unsafeWindow.gmonkey) {
        unsafeWindow.gmonkey.load('1.0', init);
    }
}, true);

function showViewType() {
  var str = '';
  switch (gmail.getActiveViewType()) {
    case 'tl': str = 'Threadlist'; break;
    case 'cv': str = 'Conversation'; break;
    case 'co': str = 'Compose'; break;
    case 'ct': str = 'Contacts'; break;
    case 's': str = 'Settings'; break;
    default: str = 'Unknown';
  }
  alert(str);
}

function html_entity_decode(str)
{
    var tarea = document.createElement('textarea');
    tarea.innerHTML = str; return tarea.value;
    tarea.parentNode.removeChild(tarea);
}

function strip(html)
{
   var tmp = document.createElement("DIV");
   tmp.innerHTML = html;
   return tmp.textContent||tmp.innerText;
}


function removeLinks(element) {
  anchors = element.getElementsByTagName('a');
  for (var i in anchors) {
    alert(anchors[i].href);
    text = document.createTextNode(anchors[i].href);
    anchors[i].parentNode.replaceChild(text, anchors[i]);
  }
}

//There is a new comment on the post "Feto é autor de processo por ofensa à honra. Sim, FETO.".
//http://www.deuslovult.org/2011/10/20/feto-e-autor-de-processo-por-ofensa-a-honra-sim-feto/

function linkPostTitle(str) {
  var array = str.match(/.*\n/g);
  title = array[0].match(/\".*\"/g)[0];
  title = title.substring(1,title.length - 1);
  link = array[1].replace(/\n/g,'');
  begin = '<b>Post:</b> <a href="'+link+'">'+title+'</a>';
  str = str.substring(str.indexOf(link)+link.length,str.length-1);
  str = begin + '\n' + str;
  return str;
}

function onConversation() {
  if(gmail.getActiveViewType() == 'cv') {
    c = gmail.getActiveViewElement();
    array = c.getElementsByClassName('gt');
    for (var i in array) {
      s = array[i].innerHTML;
      p1 = 'There is a new comment on the post';
      p2 = 'To manage your subscriptions or to block';
      r1 = (s.indexOf(p1) != -1);
      r2 = (s.indexOf(p2) != -1);
      if(r1 && r2) {      

        //removeLinks(array[i]);  
              
        //s = html_entity_decode(s);        

        s = strip(s); 

        stop = 'See all comments on this post here:';
        s = s.substring(0,s.indexOf(stop));

        s = linkPostTitle(s);

        s = s.replace(/\n{3,}/g,'\n\n');
        s = s.replace(/\n{1,}<blockquote>/g,'<blockquote>');
        s = s.replace(/<\/blockquote>\n{1,}/g,'</blockquote>');
        s = s.replace(/\n/g,'<br>');
       
        s = s.replace('Comment:<br>','<br>');
        s = s.replace('<br>Comment:','<br>');
        s = s.replace('<br>Author: ','<br><b>Comentador:</b> ');
        
        span = '- Mostrar texto das mensagens anteriores -';
        s = s.replace(span,'');
        
        array[i].innerHTML = s;

      }
    }
  }
}
 
function getCanvas() {
    try {
        gmail.getCanvasElement().addEventListener('click', onConversation, true);
        gmail.registerViewChangeCallback(onConversation);
    }
    catch (ex) {        
        GM_log('3:'+ex.message);
        alert('getCanvas()\'s exception + 3 segundos');
        window.setTimeout(window.location.reload(),3000);
    }
}
 
function init(g) {
    try {
        gmail = g;
        //alert('init() + 3 segundos');
        window.setTimeout(getCanvas, 3000);
        
    }
    catch (ex) {
        GM_log('1:'+ex.message);
        alert('init() falhou!');
    }
}

