function fixString(string) {
	string = string.trim();
	string = string.replace(/(\r\n|\n|\r)/gm,"");
	var intIndexOfMatch = string.indexOf("		");
	while (intIndexOfMatch != -1) {
		string = string.replace( "		", "	" )
		intIndexOfMatch = string.indexOf( "		" );
	}
	string = string.replace(/	/g, "/");
	
	return(string);
}

