function convertTime (datestr,AddHours){
  return parseInt (datestr.getTime() / 1000) + (AddHours*3600) + uW.g_timeoff;
}
function getDuration (datestr,AddHours){
  var Interval = convertTime(new Date(datestr.replace(" ","T")),AddHours) - uW.g_timeoff - unixTime();
  if (Interval >= 0) {
    return uW.timestr(Interval);
  }
  else {
    if (Interval > -43200) {
      return "Can't Truce for "+uW.timestr(43200 - (Interval*-1));
	}
	else {
		return "";
	}
  }
}
function GetStatusText(warStatus,truceExpireTimestamp) {
   var dur = getDuration(truceExpireTimestamp,8);
	var d = '';
	if (dur != "") {d = ' ('+dur+')';}
	else {warStatus = 1;}

    switch (parseInt(warStatus)) {
        case 1:
          return uW.g_js_strings.commonstr.normal+d;
        case 2:
          return uW.g_js_strings.MapObject.begprotect+d;
        case 3:
          return uW.g_js_strings.commonstr.truce+d;
        case 4:
          return uW.g_js_strings.commonstr.vacation+d;
        default:
          return uW.g_js_strings.commonstr.normal+d
    }
}
