function checkForUpdate() {
    var inboxFolderLabel = document.getElementById('zti__main_Mail__2_textCell');
	var currentCount = inboxFolderLabel.textContent.slice(inboxFolderLabel.textContent.indexOf('(')+1, -1);
	if (currentCount != lastCount) { 
        updateIcon(currentCount);
        lastCount = currentCount;
    }
}

function updateIcon(currentCount) {
	var intCount = 0;
	if (parseInt(currentCount)) { intCount = parseInt(currentCount); }
	var index = 0;
	if (intCount > 98) {
		index = 99; // "99+"
	} else {
		index = intCount;
	}	
	var newIcon = document.createElement("link");
	newIcon.type = "image/x-icon";
	newIcon.rel = "shortcut icon";
	newIcon.href = favicons[index];
	if (lastIcon) { document.head.removeChild(lastIcon); }
	document.head.appendChild(newIcon);
	lastIcon = newIcon;
}

// CODE STARTS EXECUTING HERE
// kill the current favicon
var x = document.head.getElementsByTagName('link');
for (var i = 0; i < x.length; i++) {
	if (x[i].rel == 'shortcut icon' || x[i].rel == 'icon') {
		document.head.removeChild(x[i]);
	}
}

// check for changes to the number of unread messages every 800 milliseconds
setInterval(checkForUpdate,800);