function oaTrim (str) {
	var	str = str.replace(/^\s\s*/, ''),
		ws = /\s/,
		i = str.length;
	while (ws.test(str.charAt(--i)));
	return str.slice(0, i + 1);
}

function oaRandomNumber() {
	aleat = Math.random() * (MAX-MIN);
	aleat = Math.round(aleat);
	return parseInt(MIN) + aleat;
} 

function oaPlaySound() {
	var emb = $id("oaSound");
	if ( emb != null ) document.removeChild(emb);
	emb = document.createElement("embed");
	emb.src = theSound;
	emb.setAttribute("autostart", "true");
	emb.setAttribute("hidden", "true");
	emb.setAttribute("cache", "true");
	switch ( theSound ) {
		case attackSoundRIP:
		case attackSound:	emb.setAttribute("volume", volAttacks);
							break;
		case probeSound:	emb.setAttribute("volume", volProbes);
							break;
		default:			emb.setAttribute("volume", volMessages);
							break;
	}
	document.body.appendChild(emb);
}

function oaSendMail(from,to,subject,content) {
	if ( EmailURL.indexOf("http://") < 0 ) {
		bLog(aLang[6]);
		return; 
	}
	if ( to.indexOf("@") < 0 ) {
		bLog(aLang[7]);
		return;
	}
	var httpRequest = new XMLHttpRequest();
	httpRequest.open("GET",EmailURL + "?name="+subject+"&email="+from+"&comments="+content+"&emailTo="+to+"&submit=send", true);
	httpRequest.onreadystatechange = function() {
		if (httpRequest.readyState == 4 && httpRequest.status == 200) {
			bLog("Email sent!");
		}
	};
	httpRequest.send(null);
	bLog("Sent Email>from: " + from + ", subject: " + subject + ", content: " + content);
}

function checkForAlarm(tData, emailOK, attackOK, espionageOK){
	var holder = document.createElement('div');
	holder.innerHTML = tData;
	var tEvents = holder.getElementsByClassName("eventFleet");
	bLog("I found " + tEvents.length + " events!");
	var tPlayerType, tMission;
	var secs = 0;
	var mins = 0;
	var hrs  = 0;
	var days = 0;
	var theContent = aLang[8];
	var theAttackTime = "none";
	var theTimes = new Object();
	var tTime = tData.match(/[0-9]*"\), [0-9]*/g);
	if ( !tTime ) {
		bLog("theTimes is null");
	} else {
		for ( var g in tTime ) {
			tTime[g] = tTime[g].split('"), ');
			theTimes[parseInt(tTime[g][0])] = parseInt(tTime[g][1]);
		}
	}

	for ( var i = 0 ; i < tEvents.length ; ++i ) {
		tPlayerType = tEvents[i].getElementsByClassName("hostile");
		if ( tPlayerType.length < 1 ) continue;
		tMission = tEvents[i].getElementsByClassName("missionFleet");
		if ( tMission.length < 1 ) continue;
		if ( espionageOK && (theSound != attackSound) && (tMission[0].firstChild.src.indexOf("spionage") != -1) ) {
			bLog("Found Incoming Espionage mission!");
			theSound = probeSound;
		} else if ( (emailOK || attackOK) && (tMission[0].firstChild.src.indexOf("angriff") != -1 || tMission[0].firstChild.src.indexOf("verband") != -1 || tMission[0].firstChild.src.indexOf("zerstoeren") != -1)) {
			if ( emailOK ) {
				secs = theTimes[parseInt(tPlayerType[0].id.split("-")[1])];
				if ( isNaN(secs) ) {
					theAttackTime = aLang[9];
					theContent = aLang[10];
				} else {
					mins = 0;
					hrs  = 0;
					days = 0;
					if ( secs > 59 ) {
						mins = Math.floor ( secs / 60 );
						secs = secs - mins * 60;
					}
					if ( mins > 59 ) {
						hrs = Math.floor ( mins / 60 );
						mins = mins - hrs * 60;
					}
					if ( hrs > 23 ) {
						days = Math.floor ( hrs / 24 );
						hrs = hrs - days * 24;
					}
					var player = tEvents[i].getElementsByClassName("playername2");
					if ( player && player.length > 0 ) { 
						player = aLang[19]+": "+player[0].innerHTML+"\n<br>  ->  ";
					} else {
						player = "";
					}
					theAttackTime = ((days>0)?(days+"d"):("")) +""+ ((hrs>0)?(hrs+"h"):("")) +""+ ((mins>0)?(mins+"m"):("")) +""+ ((secs>0)?(secs+"s"):(""));
					theContent +=  theAttackTime + "!\n<br>  ->  "+player+""+aLang[11]+": " + tEvents[i].getElementsByClassName("arrivalTime")[0].innerHTML + "\n<br>  ->  "+aLang[12]+": " + parseInt(oaTrim(tEvents[i].getElementsByClassName("detailsFleet")[0].getElementsByTagName("span")[0].innerHTML).replace(".","")) + "\n<br>  ->  "+aLang[13]+": " + tEvents[i].getElementsByClassName("originFleet")[0].innerHTML + " " + tEvents[i].getElementsByClassName("coordsOrigin")[0].firstChild.innerHTML + "\n<br>  ->  "+aLang[14]+": " + tEvents[i].getElementsByClassName("destFleet")[0].innerHTML + " " + tEvents[i].getElementsByClassName("destCoords")[0].firstChild.innerHTML;
				}	
			}
			if ( attackOK ) theSound = attackSound;
			bLog("Found Incoming Attack! attackTime = " + theAttackTime);
			break;
		}
	}
	if ( theSound != 0 ) oaPlaySound();
	if ( theAttackTime != "none" ) oaSendMail(aLang[15]+"@" + theAttackTime + "." + theServer,$id("oaEmail").value,  aLang[16], theContent);
}

function oaAlarmCheck (tOnOffs){
	var sUrl = document.getElementsByClassName("tipsStandard eventToggle")[0].href.split("/");
	sUrl = sUrl[sUrl.length-1];
	theSound = 0;
	var i;
	if ( tOnOffs[4] && (parseInt(oaTrim($id("message-wrapper").getElementsByTagName("span")[0].innerHTML)) > 0) ) theSound = messageSound;
	var emailOK		= tOnOffs[1];
	var attackOK	= tOnOffs[2];
	var espionageOK	= tOnOffs[3];
	if ( emailOK || attackOK || espionageOK ) {
		var tEventFrame = $id("iframeEventBox");
		if ( tEventFrame ) {
			bLog("Found Event Box on main view, checking for alarms there instead.")
			checkForAlarm(tEventFrame.contentWindow.document.body.innerHTML, emailOK, attackOK, espionageOK);
		} else {
			var httpRequest = new XMLHttpRequest();
			httpRequest.open("GET",sUrl, true);
			httpRequest.onreadystatechange = function() {
				if (httpRequest.readyState == 4 && httpRequest.status == 200) { // ok
					checkForAlarm(httpRequest.responseText, emailOK, attackOK, espionageOK);
				} else if ( httpRequest.status != 200 && theSound != 0 ) {
					oaPlaySound();
					theSound = 0;
				}
			};
			httpRequest.send(null);
		}
	} else {
		if ( theSound != 0 ) oaPlaySound();
	}
}

function oaSetServer () {
	theServer = location.hostname.replace(/\.|ogame|www/g,"");
	theLang = $id("playerName");
	if ( theLang != null ) theLang = theLang.getElementsByTagName("span");
	if ( theLang != null && theLang.length > 0 ) theLang = oaTrim(theLang[0].innerHTML);
	else theLang = "User";
	theServerString = theServer + "_" + theLang + "_";
	theLang = location.hostname.split(".");
	theLang = theLang[theLang.length-1];
	if ( location.hostname.indexOf('ogame') > -1 ) setVal("server",location.hostname);
	bLog("theServerString = " + theServerString +", theLang = " + theLang);
}

