// Добавляет текст: с восковичкой у Вашей девочки все в порядке...
$('<img>').attr({
	src: "data:image/png;base64,",
	class: "btnbbcode3 editor_Button",
    style: "margin-right: 2px; margin-left: 2px",
    alt: "С восковичкой у Вашей девочки все в порядке...", 
	title: "С восковичкой у Вашей девочки все в порядке...",
	onmouseout: "helpline2();",
	onmouseover: "helpline2('С восковичкой у Вашей девочки все в порядке...');",
    onclick: "bbfontstyle('с восковичкой у Вашей девочки все в порядке. :)\\n[quote=\"[url=http://parrots.ru/forum/viewtopic.php?p=123973#123973]FAQ - ОТВЕТЫ НА ЧАСТО ЗАДАВАЕМЫЕ ВОПРОСЫ О ВОЛНИСТИКАХ[/url]\"] У самочек восковица может быть белой, бледно-голубой (холодный голубой цвет), бежевой или же коричневой. Восковица является гормонально зависимым органом, коричневой она становится тогда, когда уровень гормонов повышается и самочка готова к размножению. Правда, иногда коричневой восковица становится задолго до начала репродуктивного возраста – к гнездованию допускают волнистых попугаев не моложе 1 года. Потемнение восковицы у самки часто сопровождается изменением ее поверхности – восковица становится шершавой, «сморщенной», более выпуклой. Это нормальное явление у самок. Восковица самки может изменять свой цвет в течение жизни многократно - с коричневого на более светлые и обратно, по мере изменения уровня гормонов.[/quote]\\n', '')"
})
.appendTo("#abbc3posttable .cat:last");

