function advancedCodes()
{
    var cc = document.getElementsByClassName("prettyprint");
    for (i = 0; i < cc.length; i++)
    {
	   if (i > cc.length){break;}
       processBlock(cc[i]);
    }
}

function processBlock(item)
{
	if (item.getAttribute("class").indexOf("act") >= 0)
        {
            return;
        }
    else
        {
            try
            {
				item.setAttribute("class", "act prettyprint");
				
                var retObj = reText(item);
               
			    var linenums = " ";
                if (retObj.lines > 1 && SM.GetItem("elines") != "no")
                {
                    linenums = "linenums";
                }
				
				//let's remove all items.
                for (a = 0; a < item.children.length; a++)
                {
                    item.removeChild(item.children[a]);
                }
				//update <pre> text
                item.textContent = retObj.text;
				
				var text_copy = retObj.text;			
				var lang = guess_lang(text_copy); // guess_lang seems to change the text to null, so I pass a copy
				
				var lang_text = "";
				
				if (lang == null)
				{
					//sometimes lang is null
					lang_text = "";
				} 
				else
				{
					lang_text = "lang-" + lang.prname;
				}
				
                item.setAttribute("class", "act prettyprint " + linenums + " " + lang_text);
                item.setAttribute("act_text", escape(retObj.text));
                item.setAttribute("act_lang", JSON.stringify(lang));

				if (SM.GetItem("act_editor") == "true") {addRawEdit(item);}
                prettyPrint();
				add_pre_toolbox(item);
            }
            catch (e)
            {
                if (console != null)
                {
					console.log("ACT: Error while processing block: " + item.parentNode.getAttribute("id") + ", {" + e.message + "}");
					item.setAttribute("class", "act prettyprint acterror");
					item.onmouseover = null;
					item.ondblclick = null;
					prettyPrint();
                  //  console.log(e);
                }
            }
        }
}

