function reprocessPre(pre, newlang)
{
	if (pre != null && newlang != null)
	{
		
		var ln = "";
		if (pre.getAttribute("class").indexOf("linenums") >=0)
		{
			ln = "linenums";
		}
		pre.setAttribute("class", "act prettyprint lang-" + newlang + " " + ln);
		var storedText = unescape(pre.getAttribute("act_text"));
		pre.textContent = storedText;
	
		
		pre.setAttribute("act_lang", JSON.stringify(map_lang(newlang)));
		
		pre.ondblclick = null;
		addRawEdit(pre, storedText, (newlang == " " ? " " : "lang-" + newlang));
		prettyPrint();
	}
	
	function map_lang(l)
		{
			for (i = 0; i < languages.length ; i++ )
			{
				if (languages[i].prname == l.replace("lang-",""))
				{
					return languages[i];
					break;
				}
			}
			return {prname:"", hname:"C-Like"};;
		}
}

function addRawEdit(pp)
{
	pp.ondblclick = function ()
    {
	if (SM.GetItem("act_editor") != "true") 
	{
		return;	
	}
	  var parentDiv = document.createElement("div");
      parentDiv.setAttribute("class","act npre prettyprint prettyprinted");
	  
	  pp.parentNode.insertBefore(parentDiv, pp);
	  
	  pp.parentNode.setAttribute("notitle","yes");
	  var lang = JSON.parse(pp.getAttribute("act_lang"));
	  
	  var cm_lang = try_get_MC_code(lang.prname);
	   
	  var cm = CodeMirror(parentDiv,
	  {
      value: unescape(pp.getAttribute("act_text")),
      mode: cm_lang,
	  lineNumbers: true,
	  autofocus: true
	  });
	   
	  var textButtons = SM.GetItem("textbuttons") == "yes";
      var sepera = (textButtons) ? "|" : " ";	  
	  //editor head 
	  var header = document.createElement("div"); 
	  
	  //title span
	  var tit = document.createElement("span");
	  tit.textContent = "Active syntax: '" + cm_lang +"'";
	  tit.setAttribute("style","font-size:small");
	  header.appendChild(tit);
	  
	  //toolbox div
	  var toolbox = document.createElement("div");
	  toolbox.setAttribute("style","font-size:small;float: right");
	
		var closeit = function()
		{
			parentDiv.parentNode.removeChild(parentDiv);
            pp.style.display = "";
		    pp.parentNode.setAttribute("notitle","");
		};
		
	  //Save button
	  toolbox.appendChild(getButton(
	  "Save",
	  "green",
	  saveImage_src,
	  function()
	   {
	   
			var newText = cm.doc.getValue();
			
			for (a = 0; a < pp.children.length; a++){pp.removeChild(pp.children[a]);}
			pp.textContent = newText;
			
			var t = pp.getAttribute("class").replace("prettyprinted","");
			pp.setAttribute("class", t);
            prettyPrint();
			
			pp.setAttribute("act_text",escape(newText));
				
			closeit();
			
	   },
	   textButtons));
	   
	   toolbox.appendChild(document.createTextNode(sepera));
	   
	  //undo button 
	  toolbox.appendChild(getButton(
	  "Undo",
	  "blue",
	  undoImage_src,
	  function(){cm.doc.undo();},
	  textButtons));
	   
	toolbox.appendChild(document.createTextNode(sepera));

	  //redo button 
	  toolbox.appendChild(getButton(
	  "Redo",
	  "blue",
	  redoImage_src,
	  function(){cm.doc.redo();},
	  textButtons));
	  
	  toolbox.appendChild(document.createTextNode(sepera));
	  
	  //close button 
	  toolbox.appendChild(getButton(
	  "Close editor",
	  "red",
	  closeImage_src,
	  closeit,
	  textButtons));
	  
	  
	  header.appendChild(toolbox);
	  
	  parentDiv.insertBefore(document.createElement("br"),parentDiv.children[0]);
	  
	  parentDiv.insertBefore(header,parentDiv.children[0]);
	  
	  pp.style.display = "none";
	   
    };
}


function getButton(text, tcolor, imagesrc, action, boolt)
{
	if (boolt)
	{
	  var s = document.createElement("span");
	  s.textContent = text;
	  s.style.color = tcolor;
	  s.style.cursor = "pointer";
	  s.onclick = action;
	  return s;
	}
	else
	{
	  var s = document.createElement("img");
	  s.setAttribute("src", imagesrc);
	  s.setAttribute("title", text);
	  s.style.cursor = "pointer";
	  s.setAttribute("style","max-width:16px;max-height:16px;");
	  s.onclick = action;
	  return s;
	}
}

function try_get_MC_code(lang)
{
	switch (lang.toString())
	{
		case "lisp":
			return "commonlisp";
			break;
		case "clj":
			return "clojure";
			break;
		case "scm":
			return "scheme";
			break;
		case "css":
			return "css";
			break;
		case "erlang":
			return "erlang";
			break;
		case "go":
			return "go";
			break;
		case "hs":
			return "haskell";
			break;
		case "lua":
			return "lua";
			break;
		case "python":
			return "python";
			break;
		case "php":
			return "php";
			break;
		case "shell":
			return "shell";
			break;
		case "vb":
			return "vb";
			break;
		case "sql":
			return "sql";
			break;
		default:
			return "clike"; //c-like
			break;
	}
}

function reText(elem)
{
    var sb = new Array("");
    var retobj = new reTextObj();
    for (a = 0; a < elem.children.length; a++)
    {
        switch (elem.children[a].tagName)
        {
        case "BR":
            sb.push("\n");
            retobj.lines += 1;
            break;
        case "SPAN":
            sb.push(elem.children[a].textContent);
            break;
        default:
            sb.push(elem.children[a].textContent);
            break;
        }
    }
    retobj.text = sb.join("");
    return retobj;
}

function reTextObj()
{
    this.text = "";
    this.lines = 0;
}

function guess_lang(f)
{

	//var langfinder = /lang-[^<?]+\s/;
	try
	{
		if (f.indexOf("lang-") >= 0)
		{
			for (i = 0; i < languages.length; i++)
			{
				if (f.indexOf("lang-" + languages[i].prname) >=0){return languages[i]; break;}
			}
			return {prname:"", hname:"C-Like"};
			//return langfinder.exec(f)[0].substr(0,18).toLowerCase();
		}
		else
		{
			//check for specific keywords
			if (f.indexOf("(define") >= 0)
			{
				return {prname:"scm", hname:"Scheme"};
			}
			else if (f.indexOf("(defn") >= 0)
			{
				return {prname:"clj", hname:"Clojure"};
			}
			else if (f.indexOf("(lambda") >= 0 | f.indexOf("(defmacro") >= 0 | f.indexOf("(macroexpand") >= 0)
			{
				return {prname:"lisp", hname:"Lisp"};; //scheme and lisp have the almost the same keyword, but usually `lang-lisp` work for both.
			}
			else if (f.indexOf("Dim") >= 0)
			{
				return {prname:"vb", hname:"Visual Basic"};
			}
			else if(/INSERT|SELECT|DELETE|UPDATE|INTO|DROP/g.test(f))
			{
				return {prname:"sql", hname:"SQL"};
			}
			else
			{
				return {prname:"", hname:"C-Like"};
			}
		}
	}
	catch (e)
	{
	    return {prname:"", hname:"C-Like"};
	}
}

