		columnModel.setColumnVisible(10, false);
		columnModel.setColumnWidth(11, 70);
		columnModel.setDataCellRenderer(11, new bos.ui.table.cellrenderer.ClickableLook());
		
		columnModel.setColumnWidth(12, 64);
