                var raidcontainer = btn.raidcontainer;

                btn = new qx.ui.basic.Label( "L" + d.get_Level() + " " + dungShortName( d.get_Type() ) ).set( {rich:true,textColor:"blue"} );
                btn.d = d;
                btn.mercWin = this;
