						pos = this.chat.chatLine.getValue().length + txt.length + 3;
						this.chat.chatLine.setValue(this.chat.chatLine.getValue() + " " + "[" + txt + "][/" + txt + "]");
						this.chat.chatLine.focus();
						this.chat.chatLine.setTextSelection(pos, pos);
