								5 :[68, 400, 2720, 5280, 13280, 20000, 27200, 40000, 60000, 80000], // Templer
								9 :[100, 450, 3000, 7500, 15500, 20000, 30000, 40000, 60000, 80000], // Armbrüste
								11:[28, 167, 1112, 2223, 5556, 8334, 11112, 16667, 25000, 33334], // Ritter
								7 :[36, 215, 1429, 2858, 7143, 10715, 14286, 21429, 32143, 42858], // Magier
								12:[40, 225, 1034, 2067, 5167, 8250, 10034, 15500, 20750, 275000], // Hexenmeister
								10:[55, 350, 2000, 4000, 10000, 15000, 18500, 30000, 40000, 55000], // Paladine
