			'<DIV id="idAllResultsDiv" style="height:575px; max-height:575px; overflow-x:auto; overflow-y:auto; white-space:nowrap;"></DIV>';
		document.getElementById('idAMSortBy').addEventListener ('change', t.handleAMSortBy, false);
