// ==UserScript==
// @name           Google+ Expander+
// @version        0.3
// @description    Show large image on hover, automatically load more posts at bottom of the page and scroll to top or bottom with a button click.
// @include        http://plus.google.com/*
// @include        https://plus.google.com/*
// @require        http://code.jquery.com/jquery-1.6.min.js
// ==/UserScript==

var gpluslens = function() {
  var indicator = "data:image/gif;base64,%3D%3D";
    
      function show(node,style) { node.style.display = style?style:'block'; }
  function hide(node) { node.style.display = 'none'; }
  function apply(set, update) { for (var s in update) set[s] = update[s]; }

  var frame = {
    x: 20, y: 20,
    loading: false,
    move: function(x,y) {
      this.x = x; this.y = y;
      this.shift();
    },
    shift: function() {
      var node = this.div
      var w = this.loading ? 64 : node.offsetWidth;
      var h = this.loading ? 64 : node.offsetHeight;
      var left = this.x+10;
      var top = this.y-h/2;
      if (left < 20) left = 20;
      else if (left+w > window.innerWidth-20) {
        if (this.x-w > 10) left = this.x - w - 10;
      }
      if (top < 20) top = 20;
      else if (top+h > window.innerHeight-20)
        top = window.innerHeight-20-h;

      apply(node.style, {left: left+'px', top: top+'px'});
    },
    load: function(src, title) {
      hide(frame.img);
      hide(frame.title);
      if (title) frame.title.innerHTML = title;
      else frame.title.innerHTML = "";
      this.loading = true;

      frame.img.src = src;
      apply (frame.div.style, {
        background: 'url('+indicator+') white no-repeat 50% 50%',
        height: '64px', width: '64px',
        minHeight: ''
      });

      show(frame.div);
    },
    onload: function() {
      apply (this.div.style, {
        background: 'white',
        minHeight: this.img.height+'px',
        height: '', width: this.img.width+'px'
      });
      this.loading = false;
      show(this.img);
      if (this.title.innerHTML)
        show(this.title);
      else
        hide(this.title);
      this.shift();
    }
  }
  function process() {
    if (! frame.div) {
      createlensframe(frame);
      frame.shift();
    }

    xpathmap (function (node) {
      var src = node.src.replace(/\bsz=[0-9]+/,'sz=200');
      tag (node, src, node.parentNode.title);
      node.parentNode.title = '';
    }, "//a/img[contains(@src,'photo.jpg')]");
    xpathmap (function (node) {
      var src = node.src.replace(/\bsz=[0-9]+/,'sz=200');
      tag (node, src, node.alt);
    }, "//img[contains(@src,'photo.jpg')]");
    xpathmap (function (node) {
      var src = node.src;
      var title = null;
      var proxy = src.match(/proxy\?url=([^&]*)/)
      if (proxy) {
        src = decodeURI(proxy[1]);
      } else {
        var tail = node.src.match(/\/[^\/]*$/);
        src = src.replace(/\/[wh][0-9]+\/[^\/]*$/,'/'+tail[0]);
        title = node.alt;
      }
      tag (node, src, title);
    }, "//div[contains(@data-content-type,'image/')]/img");
  }

  function tag(node, src, title) {
    if (node.lensTag)
      return;
    else
      node.lensTag = true;

    var x,y;
    var timer;
    var cancel = function(e) {
      window.clearTimeout (timer);
      hide(frame.div);
    }
    node.addEventListener ('mouseover', function(e) {
      x = e.clientX; y = e.clientY;
      timer = window.setTimeout (function () {
        frame.load (src, title);
        frame.move (x, y);
      }, 500);
    }, true);
    node.addEventListener ('mousemove', function(e) {
      frame.move (x = e.clientX, y = e.clientY);
    }, true);
    node.addEventListener ('click', cancel, true);
    node.addEventListener ('mouseout', cancel, true);
  }

  function xpathmap(fn, xpath) {
      var obj = document.evaluate(xpath, document, null,
                              XPathResult.UNORDERED_NODE_SNAPSHOT_TYPE,
                              null);
      for (var i=0; i < obj.snapshotLength; i++){
        fn (obj.snapshotItem(i));
      }
  }

  function createlensframe(display) {
    var div = display.div = document.createElement('div');
    var img = display.img = document.createElement('img');
    var title = display.title = document.createElement('div');
    div.appendChild (img);
    div.appendChild (title);

    apply (div.style, {
      position: 'fixed', zIndex: 1203,
      top: '20px', left: '20px',
      border: '1px solid #d2d2d2',
      padding: '5px', display: 'none'
    });
    apply (title.style, {
      borderTop: '1px solid #d2d2d2',
      paddingTop: '5px', marginTop: '5px',
      textAlign: 'center'
    });

    img.addEventListener ('load', function() { frame.onload(); }, true);
    div.addEventListener ('mouseover', function() { hide (frame.div); }, true);

    document.body.appendChild(div);
  }

  function domlistener() {
    var ename = 'DOMSubtreeModified';
    var content = document.getElementById('content')
    content.removeEventListener(ename, domlistener, false);
    // add grace period to allow multiple changes before reacting.
    setTimeout (function() {
      process();
      content.addEventListener(ename, domlistener, false);
    }, 500);
  }

  var poller = setInterval (function() {
    // check periodically until 'content' div appears, which we'll use
    // for dom notifications.
    if (document.getElementById('content')) {
      clearInterval (poller);
      domlistener();
    }
  }, 500);
}();
window.addEventListener("scroll", function() {
    var atBottom = parseInt((window.scrollY + window.innerHeight) / document.height + 0.1)
    if (atBottom == 1) {
        var obj = document.getElementsByClassName("a-f-zd-gb-h")[0];
        var evt = document.createEvent("MouseEvents");
        evt.initMouseEvent("click", true, true, window,
    0, 0, 0, 0, 0, false, false, false, false, 0, null);
        obj.dispatchEvent(evt);
    }
}, false);
var scroll_speed = 500;//The smaller, the faster.When you click the button, it works.
var move_speed = 50;//The smaller, the faster. When your mouse moves on the button, it works.
var toumingc_control = 1;//If you don't want to get the opacity(tou'ming in Chinese) changed, set it to 0;

if(/https?:\/\/twitter\.com/i.test(window.location.href)) document.getElementById("doc").style.position = "static";

//////////////////////////////////////////////
function up() {

    $(window).scrollTop($(window).scrollTop() - 1);
    fq = setTimeout(up, move_speed)
};

function dn() {

    $(window).scrollTop($(window).scrollTop() + 1);
    fq = setTimeout(dn, move_speed)
};

function create_button() {
	if(document.body){
		var a = document.createElement('span');
		var b = document.createElement('span');
		a.id = "shang";
		b.id = "xia";
		var css_a = 'opacity:1;-moz-transition-duration:0.2s;background:url(data:img/png;base64,) no-repeat scroll 50% 50% rgba(0, 0, 0, 0.7);border-radius:5px 0 0 5px;cursor:pointer;height:36px;margin-top:-24px;width:36px;position:fixed;right:0;bottom:53%';
		var css_b ='opacity:1;-moz-transition-duration:0.2s;background:url(data:img/png;base64,) no-repeat scroll 50% 50% rgba(0, 0, 0, 0.7);border-radius:5px 0 0 5px;cursor:pointer;height:36px;margin-top:-24px;width:36px;position:fixed;right:0;top:53%';

		a.style.cssText = css_a;
		b.style.cssText = css_b;
		a.addEventListener('mouseover',up, false);
		b.addEventListener('mouseover',dn, false);
		a.addEventListener('mouseout',function(){clearTimeout(fq);},false);
		b.addEventListener('mouseout',function(){clearTimeout(fq);},false);
		a.addEventListener('click', function(){ $("html,body").animate({scrollTop:0},scroll_speed); }, false);
		b.addEventListener('click', function(){ $("html,body").animate({scrollTop:$(document).height()},scroll_speed); }, false);

		if(toumingc_control){
		$(window).scroll(function(){
			if($(window).scrollTop()){
				a.style.display = "";
			}
			else{
				a.style.display ="none";
			}
			a.style.opacity=($(window).scrollTop())/($(document).height()-$(window).height());
			b.style.opacity=1 - ( a.style.opacity );
		});
		}
		document.body.appendChild(a);
		document.body.appendChild(b);
	}
};
if(self==top)	create_button();