function checkForUpdate() {
    var currentCount = document.getElementsByClassName("_n_i5")[0].innerHTML;

    if (currentCount == null) {
        currentCount = 0;
    } else {
        currentCount = parseInt(currentCount);
    }

    if (currentCount != lastCount) {
        lastCount = currentCount;
        updateIcon(currentCount);
    }
}


function newIcon(index) {
    var newIcon = document.createElement("link");
    newIcon.type = "image/x-icon";
    newIcon.rel = "shortcut icon";
    newIcon.href = favicons[index];
    return newIcon;
}


function updateIcon(currentCount) {
    var index = 0;

    if (currentCount > 98) {
        index = 99; // "99+"
    } else {
        index = currentCount;
    }

    var new_icon = newIcon(index);

    if (lastIcon) { document.head.removeChild(lastIcon); }

    document.head.appendChild(new_icon);
    lastIcon = new_icon;
}

// CODE STARTS EXECUTING HERE
// kill the current favicon
(function init() {
    var x = document.head.getElementsByTagName('link');

    for (var i = 0; i < x.length; i++) {
        if (x[i].rel == 'icon') {
            document.head.removeChild(x[i]);
        }
    }

    var new_icon = newIcon(0);
    document.head.appendChild(new_icon);
    lastIcon = new_icon;
})()

// check for changes to the number of unread messages every second
setInterval(checkForUpdate, 10000);
