	this.GenerateImageRecommendation = function(title, price, imageUrl, isFemale, parentElement) {
		parentElement.children().remove();
		parentElement.append('<div style="width: 242px; height: 300px; background-color: ' + ((isFemale) ? femaleColor : maleColor) + '"> ' +
			'<img style="z-index: 0; position: relative; left: 30px; top: 10px" width="180" height="240" src="' + imageUrl + '" />'+
			'<div style="height: 50px; font-family: Arial; font-size: 14px; text-align: center; top: 10px; color: white; position: relative; background-color: rgba(0,0,0,0.5)">' + title + '</div>' +
			'<div style="height: 20px; font-family: Arial; font-size: 13px; text-align: center; -moz-border-radius: 50px; border: 10px solid #989797; border-radius: 50px; color: white; background-color: #989797; opacity: 1; position: relative; top: -280px; left: 30px; width: 30px; z-index: 10">&pound;' + Math.round(price) + '</div>' +
			'</div>');  
	}
	
