var last_comment = null;

// Code to support GM_getvalue and GM_setvalue in Chrome
if (!this.GM_getValue || this.GM_getValue.toString().indexOf("not supported")>-1) {
    this.GM_getValue=function (key,def) {
        return localStorage[key] || def;
    };
    this.GM_setValue=function (key,value) {
        return localStorage[key]=value;
    };
}

// The theory is that this never changes -- seems to be a good theory so far
var comments_end = document.getElementById('comments-end');

// Scan through the comments and hide as appropriate
function comment_scan() {
	var nodeListDivs = document.getElementsByTagName('div');
	
	for (var i=0; i<nodeListDivs.length; i++) {
		
		// If the block is a comment, find its author
		if (nodeListDivs[i].className == "content") {
			
			// Go up one level in the tree, then look for the poster name in a span
			var nodeListSpans = nodeListDivs[i].parentNode.getElementsByTagName('span');
			
			for (var j=0; j<nodeListSpans.length; j++) {
			
				if (nodeListSpans[j].hasAttribute('class')) {
		        
		        	if (nodeListSpans[j].className == "unlinked-poster-name") {
		        		
						// If the poster's scan value is greater than the current scan level, hide the comment (including any quotes)
						if (GM_getValue(nodeListSpans[j].firstChild.wholeText,null) > GM_getValue("HCN_scanner_level",1)) {
		        			nodeListDivs[i].style.display = 'none';
						}
						else {
		        			nodeListDivs[i].style.display = 'block';
						}
					}
				}
			}
		}
	}
}

// Find all poster profiles, remove any poster icons, insert rating icons
function icon_scan() {
	
	var nodeSubmitted = document.getElementsByClassName('submitted');

	// Go through every comment
	for (var i=0; i<nodeSubmitted.length; i++) {

		// Get the info we need from underneath the submitted block
		var nodePics = nodeSubmitted[i].getElementsByClassName("picture");
		var nodePosters = nodeSubmitted[i].getElementsByClassName("unlinked-poster-name");
		
		// Remove old pic from under submitted block
		if (nodePics[0]) {		
			nodePics[0].parentNode.removeChild(nodePics[0]);
		}

		// Get poster rating (or default to null if unrated)
		var poster_rating = GM_getValue(nodePosters[0].firstChild.wholeText,null);
					
		// If we haven't already added our own icon, then add one
		if (nodeSubmitted[i].previousSibling.className != "picture") {
		
			// Create a new span to add rating icon
			var picSpan = document.createElement("span");
			picSpan.setAttribute("class","picture");
	
			// And a replacement image, based on rating
			var picImg = document.createElement("img");
			picImg.title = "";
			picImg.alt = "";
			picImg.align = "left";
			
			picSpan.appendChild(picImg);
							
			// Now put in the new one, in front of the submitted block (so it can be safely clicked on)
			nodeSubmitted[i].parentNode.insertBefore(picSpan,nodeSubmitted[i]);
		}
		else {
			// Get the img that's already there, and set it's src to the appropriate rating
			var picTmp = nodeSubmitted[i].previousSibling.getElementsByTagName("img");
			var picImg = picTmp[0];
		}

		// If this is the top CR icon, i.e. the first icon, set it to the current scan level
		if (i == 0) {

			scan_level = GM_getValue("HCN_scanner_level",1);
			
			// Set image to our inline pics as appropriate
			if (scan_level == 1) {
				picImg.src = one_scan;
			}
			else if (scan_level == 2) {
				picImg.src = two_scan;
			}
			else if (scan_level == 3) {
				picImg.src = three_scan;
			}
			else {
				picImg.src = qm_src;
			}
		}
		else {
			// Set image to our inline pics as appropriate
			if (poster_rating == 1) {
				picImg.src = one_src;
			}
			else if (poster_rating == 2) {
				picImg.src = two_src;
			}
			else if (poster_rating == 3) {
				picImg.src = three_src;
			}
			else {
				picImg.src = qm_src;
			}
		}
	}
}

function full_scan() {
	comment_scan();
	icon_scan();
}

// Get a poster's level and store it
function set_level() {
	
	// Go up one level in the tree, then look for the poster name in a span
	var nodeListSpans = this.parentNode.getElementsByTagName('span');
			
	for (var j=0; j<nodeListSpans.length; j++) {
			
		if (nodeListSpans[j].hasAttribute('class')) {
		        
			if (nodeListSpans[j].className == "unlinked-poster-name") {
				
		        var user_level = prompt("Scan level for: "+nodeListSpans[j].firstChild.wholeText, "1, 2 or 3");
		        	
		        if (user_level == null) {
		        	// alert("Input cancelled");
		        }
		        else {
		        	if (user_level >= 0 && user_level <= 3) {
		        		GM_setValue(nodeListSpans[j].firstChild.wholeText, user_level);
		        		
		        		// Rescan based on new setting
		        		full_scan();
		        	}
		        	else {
		        		alert("Huh?");
		        	}
		        }
			}
		}
	}
}

