// ==UserScript==
// @name           模拟小屏幕
// @namespace      
// @include        
// ==/UserScript==



(function ssr() {
	var head, style;
	head = document.getElementsByTagName('head')[0];
	if (!head) { return; }
	style = document.createElement('link');
	style.type = 'text/css';
	style.rel = 'stylesheet';
	style.id = 'small-screen-render';
	style.href = 'data:text/css;base64,';
	head.appendChild(style);
})();
